import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";
import dotenv from 'dotenv';

dotenv.config();

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
// Trust first proxy for correct IP detection
app.set('trust proxy', true);
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    // Check if response headers have already been sent
    if (!res.headersSent) {
      res.status(status).json({ message });
    }

    // Log the error instead of throwing it to prevent app crash
    console.error('Error handled:', err);
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: true,
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertCyberCaseSchema, insertUserSchema } from "@shared/schema";
import { z } from "zod";
import session from "express-session";
import connectPg from "connect-pg-simple";
import crypto from "crypto";

// Helper function to parse user agent with better device detection
function parseUserAgent(userAgent: string) {
  let browser = 'Desconocido';
  let os = 'Desconocido';
  let deviceType = 'PC';

  // Detectar tipo de dispositivo primero
  const mobileRegex = /Mobile|Android|iPhone|iPod|BlackBerry|Windows Phone|Opera Mini/i;
  const tabletRegex = /iPad|Tablet|PlayBook|Silk|Kindle/i;

  if (tabletRegex.test(userAgent)) {
    deviceType = 'Tablet';
  } else if (mobileRegex.test(userAgent)) {
    deviceType = 'Móvil';
  }

  // Parse browser con mejor detección
  if (userAgent.includes('Edg/')) browser = 'Edge';
  else if (userAgent.includes('Chrome') && !userAgent.includes('Edg/')) browser = 'Chrome';
  else if (userAgent.includes('Firefox')) browser = 'Firefox';
  else if (userAgent.includes('Safari') && !userAgent.includes('Chrome')) browser = 'Safari';
  else if (userAgent.includes('Opera')) browser = 'Opera';

  // Parse OS con mejor detección
  if (userAgent.includes('Windows NT')) os = 'Windows';
  else if (userAgent.includes('Mac OS X') || userAgent.includes('macOS')) os = 'macOS';
  else if (userAgent.includes('iPhone OS') || userAgent.includes('iOS')) os = 'iOS';
  else if (userAgent.includes('Android')) os = 'Android';
  else if (userAgent.includes('Linux') && !userAgent.includes('Android')) os = 'Linux';
  else if (userAgent.includes('Ubuntu')) os = 'Ubuntu';
  else if (userAgent.includes('CrOS')) os = 'Chrome OS';

  return { browser, os, deviceType };
}

// Hash password function (simple for demo - use bcrypt in production)
function hashPassword(password: string): string {
  return crypto.createHash('sha256').update(password).digest('hex');
}

// Simple auth middleware
const isAuthenticated = (req: any, res: any, next: any) => {
  if (req.session?.userId) {
    return next();
  }
  res.status(401).json({ message: "Unauthorized" });
};

export async function registerRoutes(app: Express): Promise<Server> {
  // Session setup
  app.set("trust proxy", 1);
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: true,
    ttl: sessionTtl,
    tableName: "sessions",
  });

  app.use(session({
    secret: process.env.SESSION_SECRET || "cyber-crime-secret",
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: process.env.NODE_ENV === "production",
      maxAge: sessionTtl,
    },
  }));

  // Initialize default users if not exist
  async function initializeDefaultUsers() {
    try {
      const defaultUsers = [
        {
          username: "admin",
          email: "admin@cybercrime.com",
          password: "admin123",
          firstName: "Administrador",
          lastName: "Sistema",
          role: "admin" as const,
          isActive: "true" as const
        },
        {
          username: "maria.gonzalez",
          email: "maria.gonzalez@cybercrime.com",
          password: "investigador123",
          firstName: "María",
          lastName: "González",
          role: "investigator" as const,
          isActive: "true" as const
        },
        {
          username: "carlos.rodriguez",
          email: "carlos.rodriguez@cybercrime.com", 
          password: "analista123",
          firstName: "Carlos",
          lastName: "Rodríguez",
          role: "user" as const,
          isActive: "true" as const
        },
        {
          username: "ana.martinez",
          email: "ana.martinez@cybercrime.com",
          password: "auditor123", 
          firstName: "Ana",
          lastName: "Martínez",
          role: "auditor" as const,
          isActive: "true" as const
        },
        {
          username: "jose.lopez",
          email: "jose.lopez@cybercrime.com",
          password: "investigador123",
          firstName: "José",
          lastName: "López", 
          role: "investigator" as const,
          isActive: "false" as const
        }
      ];

      for (const userData of defaultUsers) {
        const existingUser = await storage.getUserByUsername(userData.username);
        if (!existingUser) {
          await storage.createUser({
            ...userData,
            password: hashPassword(userData.password)
          });
          console.log(`Usuario ${userData.username} creado exitosamente`);
        }
      }
    } catch (error) {
      console.error("Error creating default users:", error);
    }
  }

  // Initialize default users
  await initializeDefaultUsers();

  // Login route with session tracking
  app.post('/api/login', async (req: any, res) => {
    try {
      const { username, password } = req.body;

      const user = await storage.getUserByUsername(username);
      if (!user || hashPassword(password) !== user.password || user.isActive !== "true") {
        return res.status(401).json({ message: "Credenciales inválidas" });
      }

      // Update last login
      await storage.updateUser(user.id, { lastLoginAt: new Date() });

      // Create session record
      const sessionId = req.sessionID || crypto.randomUUID();
      const userAgent = req.headers['user-agent'] || '';
      const { browser, os, deviceType } = parseUserAgent(userAgent);
      // Get real IP address considering proxies
      let ipAddress = req.headers['x-forwarded-for']?.split(',')[0]?.trim() || 
                     req.headers['x-real-ip'] || 
                     req.ip || 
                     req.connection.remoteAddress || 
                     req.socket.remoteAddress ||
                     'unknown';

      // Clean up IPv6 loopback and map to IPv4
      if (ipAddress === '::1' || ipAddress === '::ffff:127.0.0.1') {
        ipAddress = '127.0.0.1';
      }
      // Remove IPv6 mapping prefix if present
      if (ipAddress.startsWith('::ffff:')) {
        ipAddress = ipAddress.substring(7);
      }

      await storage.createUserSession({
        userId: user.id,
        sessionId,
        ipAddress,
        userAgent,
        deviceInfo: `${deviceType} - ${browser}`,
        browser,
        os,
        location: 'No disponible', // Could integrate with IP geolocation service
        isActive: "true"
      });

      req.session.userId = user.id;
      req.session.user = {
        id: user.id,
        username: user.username,
        email: user.email,
        firstName: user.firstName,
        lastName: user.lastName,
        role: user.role,
      };

      res.json({ success: true });
    } catch (error) {
      console.error("Login error:", error);
      res.status(500).json({ message: "Error interno del servidor" });
    }
  });

  // Logout handler function
  const handleLogout = async (req: any, res: any) => {
    try {
      const sessionId = req.sessionID;
      if (sessionId) {
        await storage.updateSessionLogout(sessionId);
      }

      req.session.destroy((err: any) => {
        if (err) {
          return res.status(500).json({ message: "No se pudo cerrar sesión" });
        }
        res.json({ success: true });
      });
    } catch (error) {
      console.error("Logout error:", error);
      res.status(500).json({ message: "Error interno del servidor" });
    }
  };

  // Support both GET and POST for logout
  app.post('/api/logout', handleLogout);
  app.get('/api/logout', handleLogout);

  // Auth routes
  app.get("/api/auth/user", isAuthenticated, async (req: any, res) => {
    try {
      res.json(req.session.user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // User settings routes
  app.get("/api/user/settings", isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.session?.userId || "admin";
      const settings = await storage.getUserSettings(userId);

      // Return default settings if none exist
      if (!settings) {
        const defaultSettings = {
          primaryColor: "green",
          secondaryColor: "gray",
          transparency: "85",
          neonEffects: "false",
          fontSize: "14",
          animations: "false"
        };
        res.json(defaultSettings);
      } else {
        res.json({
          primaryColor: settings.primaryColor,
          secondaryColor: settings.secondaryColor || "gray",
          transparency: settings.transparency,
          neonEffects: settings.neonEffects,
          fontSize: settings.fontSize,
          animations: settings.animations
        });
      }
    } catch (error) {
      console.error("Error fetching user settings:", error);
      res.status(500).json({ message: "Failed to fetch user settings" });
    }
  });

  app.post("/api/user/settings", isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.session?.userId || "admin";
      const { primaryColor, secondaryColor, transparency, neonEffects, fontSize, animations } = req.body;

      const settings = await storage.upsertUserSettings(userId, {
        primaryColor: primaryColor || "green",
        secondaryColor: secondaryColor || "gray",
        transparency: transparency?.toString() || "85",
        neonEffects: neonEffects?.toString() || "false",
        fontSize: fontSize?.toString() || "14",
        animations: animations?.toString() || "false"
      });

      res.json({
        primaryColor: settings.primaryColor,
        secondaryColor: settings.secondaryColor,
        transparency: settings.transparency,
        neonEffects: settings.neonEffects,
        fontSize: settings.fontSize,
        animations: settings.animations
      });
    } catch (error) {
      console.error("Error saving user settings:", error);
      res.status(500).json({ message: "Failed to save user settings" });
    }
  });

  // Dashboard stats
  app.get("/api/dashboard/stats", isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.session?.userId || "admin";
      const stats = await storage.getDashboardStats(userId);
      res.json(stats);
    } catch (error) {
      console.error("Error fetching dashboard stats:", error);
      res.status(500).json({ message: "Failed to fetch dashboard stats" });
    }
  });

  // Cyber cases routes
  app.get("/api/cyber-cases", isAuthenticated, async (req: any, res) => {
    try {
      const { page, limit, search, crimeType, dateFrom, dateTo } = req.query;
      const result = await storage.getCyberCases({
        page: page ? parseInt(page) : 1,
        limit: limit ? parseInt(limit) : 10,
        search: search || "",
        crimeType: crimeType || "",
        dateFrom: dateFrom || "",
        dateTo: dateTo || "",
      });
      res.json(result);
    } catch (error) {
      console.error("Error fetching cyber cases:", error);
      res.status(500).json({ message: "Failed to fetch cyber cases" });
    }
  });

  app.get("/api/cyber-cases/:id", isAuthenticated, async (req, res) => {
    try {
      const { id } = req.params;
      const cyberCase = await storage.getCyberCase(id);
      if (!cyberCase) {
        return res.status(404).json({ message: "Cyber case not found" });
      }
      res.json(cyberCase);
    } catch (error) {
      console.error("Error fetching cyber case:", error);
      res.status(500).json({ message: "Failed to fetch cyber case" });
    }
  });

  app.post("/api/cyber-cases", isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.session?.userId || "admin";
      const validatedData = insertCyberCaseSchema.parse(req.body);
      const cyberCase = await storage.createCyberCase(validatedData, userId);
      res.status(201).json(cyberCase);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Validation error", errors: error.errors });
      }
      console.error("Error creating cyber case:", error);
      res.status(500).json({ message: "Failed to create cyber case" });
    }
  });

  app.put("/api/cyber-cases/:id", isAuthenticated, async (req: any, res) => {
    try {
      const { id } = req.params;
      const userId = req.session?.userId || "admin";
      const validatedData = insertCyberCaseSchema.partial().parse(req.body);
      const cyberCase = await storage.updateCyberCase(id, validatedData, userId);
      res.json(cyberCase);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Validation error", errors: error.errors });
      }
      console.error("Error updating cyber case:", error);
      res.status(500).json({ message: "Failed to update cyber case" });
    }
  });

  app.delete("/api/cyber-cases/:id", isAuthenticated, async (req: any, res) => {
    try {
      const { id } = req.params;
      const userId = req.session?.userId || "admin";
      await storage.deleteCyberCase(id, userId);
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting cyber case:", error);
      res.status(500).json({ message: "Failed to delete cyber case" });
    }
  });

  // Intelligence search endpoint
  app.post('/api/intelligence/search', isAuthenticated, async (req: any, res) => {
    try {
      const { query } = req.body;

      if (!query || typeof query !== 'string') {
        return res.status(400).json({ message: 'Query is required' });
      }

      // For now, return mock search results until we implement a real search service
      const mockResults = [
        {
          title: `Información sobre: ${query}`,
          url: `https://example.com/search?q=${encodeURIComponent(query)}`,
          description: `Resultados relacionados con ${query}. Esta es una implementación temporal que muestra cómo funcionará la búsqueda inteligente.`,
          citations: ['https://cybersecurity.org', 'https://cert.gov']
        },
        {
          title: `Guías de investigación: ${query}`,
          url: `https://investigacion-cyber.com/${query.toLowerCase()}`,
          description: `Metodologías y técnicas para investigar casos relacionados con ${query}. Incluye mejores prácticas y herramientas recomendadas.`,
          citations: ['https://forensics.org']
        }
      ];

      res.json({ results: mockResults });
    } catch (error) {
      console.error('Intelligence search error:', error);
      res.status(500).json({ message: 'Error en la búsqueda' });
    }
  });

  // User management endpoints
  app.get('/api/users', isAuthenticated, async (req: any, res) => {
    try {
      const users = await storage.getAllUsers();
      // Get current session info for each user
      const usersWithSessions = await Promise.all(
        users.map(async (user) => {
          const { password, ...safeUser } = user;
          // Get the most recent active session for this user
          const userSessions = await storage.getUserSessions(user.id);
          const currentSession = userSessions.find(session => session.isActive === "true");

          return {
            ...safeUser,
            currentSession: currentSession ? {
              ipAddress: currentSession.ipAddress,
              deviceInfo: currentSession.deviceInfo,
              browser: currentSession.browser,
              os: currentSession.os
            } : null
          };
        })
      );

      res.json(usersWithSessions);
    } catch (error) {
      console.error('Error fetching users:', error);
      res.status(500).json({ message: 'Error al obtener usuarios' });
    }
  });

  app.post('/api/users', isAuthenticated, async (req: any, res) => {
    try {
      const userData = insertUserSchema.parse(req.body);
      // Hash password before storing
      const hashedPassword = hashPassword(userData.password);
      const newUser = await storage.createUser({
        ...userData,
        password: hashedPassword
      });
      // Remove password from response
      const { password, ...safeUser } = newUser;
      res.json(safeUser);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Error de validación", errors: error.errors });
      }
      console.error('Error creating user:', error);
      res.status(500).json({ message: 'Error al crear usuario' });
    }
  });

  app.put('/api/users/:id', isAuthenticated, async (req: any, res) => {
    try {
      const { id } = req.params;
      const userData = insertUserSchema.partial().parse(req.body);
      // Hash password if provided
      if (userData.password) {
        userData.password = hashPassword(userData.password);
      }
      const updatedUser = await storage.updateUser(id, userData);
      // Remove password from response
      const { password, ...safeUser } = updatedUser;
      res.json(safeUser);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Error de validación", errors: error.errors });
      }
      console.error('Error updating user:', error);
      res.status(500).json({ message: 'Error al actualizar usuario' });
    }
  });

  app.delete('/api/users/:id', isAuthenticated, async (req: any, res) => {
    try {
      const { id } = req.params;
      await storage.deleteUser(id);
      res.status(204).send();
    } catch (error) {
      console.error('Error deleting user:', error);
      res.status(500).json({ message: 'Error al eliminar usuario' });
    }
  });

  // User sessions endpoints
  app.get('/api/sessions', isAuthenticated, async (req: any, res) => {
    try {
      const sessions = await storage.getAllActiveSessions();
      res.json(sessions);
    } catch (error) {
      console.error('Error fetching sessions:', error);
      res.status(500).json({ message: 'Error al obtener sesiones' });
    }
  });

  app.get('/api/users/:id/sessions', isAuthenticated, async (req: any, res) => {
    try {
      const { id } = req.params;
      const sessions = await storage.getUserSessions(id);
      res.json(sessions);
    } catch (error) {
      console.error('Error fetching user sessions:', error);
      res.status(500).json({ message: 'Error al obtener sesiones del usuario' });
    }
  });

  // Chatbot endpoint
  app.post('/api/chatbot/message', isAuthenticated, async (req: any, res) => {
    try {
      const { message } = req.body;

      if (!message || typeof message !== 'string') {
        return res.status(400).json({ message: 'Message is required' });
      }

      // Simple rule-based chatbot responses for cyber crime topics
      let response = '';
      const lowerMessage = message.toLowerCase();

      if (lowerMessage.includes('phishing') || lowerMessage.includes('suplantación')) {
        response = 'El phishing es una técnica de ingeniería social donde los atacantes se hacen pasar por entidades confiables para obtener información sensible. Para investigar casos de phishing:\n\n1. Analiza los headers del correo electrónico\n2. Verifica el dominio remitente\n3. Examina los enlaces y archivos adjuntos\n4. Documenta las cuentas bancarias involucradas\n\n¿Necesitas ayuda con algún aspecto específico del análisis de phishing?';
      } else if (lowerMessage.includes('malware') || lowerMessage.includes('virus')) {
        response = 'Para casos de malware en delitos informáticos:\n\n1. Aísla el sistema infectado\n2. Captura evidencia forense antes de la limpieza\n3. Analiza los hashes de archivos sospechosos\n4. Documenta la cadena de infección\n5. Identifica sistemas comprometidos\n\n¿Qué tipo de malware estás investigando? (ransomware, trojan bancario, spyware, etc.)';
      } else if (lowerMessage.includes('criptomoneda') || lowerMessage.includes('bitcoin') || lowerMessage.includes('blockchain')) {
        response = 'Para investigaciones con criptomonedas:\n\n1. Rastrea las direcciones de wallet involucradas\n2. Usa herramientas de análisis blockchain (Chainalysis, Crystal)\n3. Identifica exchanges utilizados\n4. Documenta transacciones sospechosas\n5. Colabora con exchanges para obtener información KYC\n\n¿Tienes direcciones de wallet específicas para analizar?';
      } else if (lowerMessage.includes('fraude') || lowerMessage.includes('estafa')) {
        response = 'Para casos de fraude informático:\n\n1. Documenta el modus operandi del atacante\n2. Recopila evidencia de comunicaciones\n3. Analiza flujos financieros\n4. Identifica víctimas adicionales\n5. Coordina con entidades financieras\n\n¿Qué tipo de fraude estás investigando? (SIM swapping, business email compromise, romance scam, etc.)';
      } else if (lowerMessage.includes('forensic') || lowerMessage.includes('evidencia')) {
        response = 'Para análisis forense digital:\n\n1. Mantén la cadena de custodia\n2. Crea imágenes bit a bit de dispositivos\n3. Usa herramientas certificadas (EnCase, FTK, Autopsy)\n4. Documenta todos los procedimientos\n5. Preserva metadatos originales\n\n¿Qué tipo de dispositivo necesitas analizar?';
      } else {
        response = `Hola, soy tu asistente especializado en delitos informáticos. He recibido tu consulta sobre "${message}".\n\nPuedo ayudarte con:\n• Análisis de phishing y fraudes\n• Investigación de malware\n• Rastreo de criptomonedas\n• Análisis forense digital\n• Técnicas de investigación\n• Normativas legales\n\n¿Podrías ser más específico sobre qué aspecto necesitas que te ayude?`;
      }

      res.json({ response });
    } catch (error) {
      console.error('Chatbot error:', error);
      res.status(500).json({ message: 'Error en el chatbot' });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  users,
  cyberCases,
  userSettings,
  userSessions,
  type User,
  type UpsertUser,
  type CyberCase,
  type InsertCyberCase,
  type UserSettings,
  type InsertUserSettings,
  type UserSession,
  type InsertUserSession,
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, ilike, and, gte, lte, count, sql } from "drizzle-orm";

export interface IStorage {
  // User operations
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(user: Omit<UpsertUser, 'id'>): Promise<User>;
  updateUser(id: string, user: Partial<UpsertUser>): Promise<User>;
  deleteUser(id: string): Promise<void>;
  getAllUsers(): Promise<User[]>;
  upsertUser(user: UpsertUser): Promise<User>;

  // User session operations
  createUserSession(session: Omit<InsertUserSession, 'id'>): Promise<UserSession>;
  getUserSessions(userId: string): Promise<UserSession[]>;
  getAllActiveSessions(): Promise<(UserSession & { user: User })[]>;
  updateSessionLogout(sessionId: string): Promise<void>;

  // User settings operations
  getUserSettings(userId: string): Promise<UserSettings | undefined>;
  upsertUserSettings(userId: string, settings: Partial<InsertUserSettings>): Promise<UserSettings>;

  // Cyber case operations
  getCyberCases(params: {
    page?: number;
    limit?: number;
    search?: string;
    crimeType?: string;
    dateFrom?: string;
    dateTo?: string;
  }): Promise<{ cases: CyberCase[]; total: number }>;
  getCyberCase(id: string): Promise<CyberCase | undefined>;
  createCyberCase(caseData: InsertCyberCase, userId: string): Promise<CyberCase>;
  updateCyberCase(id: string, caseData: Partial<InsertCyberCase>, userId: string): Promise<CyberCase>;
  deleteCyberCase(id: string, userId: string): Promise<void>;
  getDashboardStats(userId: string): Promise<{
    totalCases: number;
    totalAmount: string;
    activeCases: number;
    resolvedCases: number;
    monthlyCases: number[];
    crimeTypeStats: { type: string; count: number }[];
  }>;
}

export class DatabaseStorage implements IStorage {
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user;
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.email, email));
    return user;
  }

  async createUser(userData: Omit<UpsertUser, 'id'>): Promise<User> {
    const [user] = await db.insert(users).values(userData).returning();
    return user;
  }

  async updateUser(id: string, userData: Partial<UpsertUser>): Promise<User> {
    const [user] = await db
      .update(users)
      .set({ ...userData, updatedAt: new Date() })
      .where(eq(users.id, id))
      .returning();
    return user;
  }

  async deleteUser(id: string): Promise<void> {
    await db.delete(users).where(eq(users.id, id));
  }

  async getAllUsers(): Promise<User[]> {
    return await db.select().from(users).orderBy(desc(users.createdAt));
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  async createUserSession(sessionData: Omit<InsertUserSession, 'id'>): Promise<UserSession> {
    const [session] = await db.insert(userSessions).values(sessionData).returning();
    return session;
  }

  async getUserSessions(userId: string): Promise<UserSession[]> {
    return await db
      .select()
      .from(userSessions)
      .where(eq(userSessions.userId, userId))
      .orderBy(desc(userSessions.loginAt));
  }

  async getAllActiveSessions(): Promise<(UserSession & { user: User })[]> {
    const sessions = await db
      .select({
        session: userSessions,
        user: users
      })
      .from(userSessions)
      .innerJoin(users, eq(userSessions.userId, users.id))
      .where(eq(userSessions.isActive, "true"))
      .orderBy(desc(userSessions.loginAt));

    return sessions.map(row => ({ ...row.session, user: row.user }));
  }

  async updateSessionLogout(sessionId: string): Promise<void> {
    await db
      .update(userSessions)
      .set({ 
        isActive: "false", 
        logoutAt: new Date() 
      })
      .where(eq(userSessions.sessionId, sessionId));
  }

  async getUserSettings(userId: string): Promise<UserSettings | undefined> {
    const [settings] = await db
      .select()
      .from(userSettings)
      .where(eq(userSettings.userId, userId));
    return settings;
  }

  async upsertUserSettings(userId: string, settingsData: Partial<InsertUserSettings>): Promise<UserSettings> {
    const [settings] = await db
      .insert(userSettings)
      .values({
        userId,
        ...settingsData,
      })
      .onConflictDoUpdate({
        target: userSettings.userId,
        set: {
          ...settingsData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return settings;
  }

  async getCyberCases(params: {
    page?: number;
    limit?: number;
    search?: string;
    crimeType?: string;
    dateFrom?: string;
    dateTo?: string;
  } = {}): Promise<{ cases: CyberCase[]; total: number }> {
    const { page = 1, limit = 10, search = "", crimeType = "", dateFrom = "", dateTo = "" } = params;
    const offset = (page - 1) * limit;

    let whereConditions = [];

    if (search) {
      whereConditions.push(
        sql`(${cyberCases.expedientNumber} ILIKE ${`%${search}%`} OR ${cyberCases.crimeType} ILIKE ${`%${search}%`} OR ${cyberCases.victim} ILIKE ${`%${search}%`})`
      );
    }

    if (crimeType) {
      whereConditions.push(eq(cyberCases.crimeType, crimeType));
    }

    if (dateFrom) {
      whereConditions.push(gte(cyberCases.caseDate, dateFrom));
    }

    if (dateTo) {
      whereConditions.push(lte(cyberCases.caseDate, dateTo));
    }

    const whereClause = whereConditions.length > 0 ? and(...whereConditions) : undefined;

    const [cases, totalResult] = await Promise.all([
      db
        .select()
        .from(cyberCases)
        .where(whereClause)
        .orderBy(desc(cyberCases.createdAt))
        .limit(limit)
        .offset(offset),
      db
        .select({ count: count() })
        .from(cyberCases)
        .where(whereClause),
    ]);

    return {
      cases,
      total: totalResult[0].count,
    };
  }

  async getCyberCase(id: string): Promise<CyberCase | undefined> {
    const [cyberCase] = await db
      .select()
      .from(cyberCases)
      .where(eq(cyberCases.id, id));
    return cyberCase;
  }

  async createCyberCase(caseData: InsertCyberCase, userId: string): Promise<CyberCase> {
    const [cyberCase] = await db
      .insert(cyberCases)
      .values({
        ...caseData,
        createdBy: userId,
      })
      .returning();
    return cyberCase;
  }

  async updateCyberCase(
    id: string,
    caseData: Partial<InsertCyberCase>,
    userId: string
  ): Promise<CyberCase> {
    const [cyberCase] = await db
      .update(cyberCases)
      .set({
        ...caseData,
        updatedAt: new Date(),
      })
      .where(eq(cyberCases.id, id))
      .returning();
    return cyberCase;
  }

  async deleteCyberCase(id: string, userId: string): Promise<void> {
    await db.delete(cyberCases).where(eq(cyberCases.id, id));
  }

  async getDashboardStats(userId: string): Promise<{
    totalCases: number;
    totalAmount: string;
    activeCases: number;
    resolvedCases: number;
    monthlyCases: number[];
    crimeTypeStats: { type: string; count: number }[];
  }> {
    // Get total cases and amount
    const [totalStats] = await db
      .select({
        totalCases: count(),
        totalAmount: sql<string>`COALESCE(SUM(${cyberCases.stolenAmount}), 0)`,
      })
      .from(cyberCases);

    // Get active cases (pending, in process)
    const [activeStats] = await db
      .select({ count: count() })
      .from(cyberCases)
      .where(
        sql`${cyberCases.investigationStatus} IN ('Pendiente', 'En proceso')`
      );

    // Get resolved cases
    const [resolvedStats] = await db
      .select({ count: count() })
      .from(cyberCases)
      .where(eq(cyberCases.investigationStatus, "Completado"));

    // Get monthly cases for the current year
    const monthlyCases = await db
      .select({
        month: sql<number>`EXTRACT(MONTH FROM ${cyberCases.caseDate})`,
        count: count(),
      })
      .from(cyberCases)
      .where(sql`EXTRACT(YEAR FROM ${cyberCases.caseDate}) = EXTRACT(YEAR FROM CURRENT_DATE)`)
      .groupBy(sql`EXTRACT(MONTH FROM ${cyberCases.caseDate})`)
      .orderBy(sql`EXTRACT(MONTH FROM ${cyberCases.caseDate})`);

    // Get crime type statistics
    const crimeTypeStats = await db
      .select({
        type: cyberCases.crimeType,
        count: count(),
      })
      .from(cyberCases)
      .groupBy(cyberCases.crimeType)
      .orderBy(desc(count()));

    // Fill monthly data for all 12 months
    const monthlyData = Array(12).fill(0);
    monthlyCases.forEach((item) => {
      monthlyData[item.month - 1] = item.count;
    });

    return {
      totalCases: totalStats.totalCases,
      totalAmount: totalStats.totalAmount,
      activeCases: activeStats.count,
      resolvedCases: resolvedStats.count,
      monthlyCases: monthlyData,
      crimeTypeStats,
    };
  }
}

export const storage = new DatabaseStorage();

