@layer base {
  :root {
    /* Hacker Dark Theme - Base colors */
    --background: 0 0% 3%;
    --foreground: 120 100% 75%;
    --card: 0 0% 5%;
    --card-foreground: 120 100% 90%;
    --popover: 0 0% 5%;
