    --accent: 60 100% 50%;
    --accent-foreground: 0 0% 0%;
    --destructive: 0 85% 65%;
    --destructive-foreground: 0 0% 100%;
    --border: 0 0% 10%;
    --input: 0 0% 6%;
    --ring: 120 100% 50%;
    --chart-1: 120 100% 50%;
    --chart-2: 180 100% 50%;
    --chart-3: 60 100% 50%;
    --chart-4: 300 100% 50%;
