  /* Global input styles - Dynamic colors */
  .input-primary {
    background: hsl(var(--input));
    border: 1px solid hsl(var(--border));
    color: hsl(var(--foreground));
  }

  .input-primary:focus {
    border-color: hsl(var(--primary));
