    border: 1px solid hsl(var(--primary) / 0.3);
    color: hsl(var(--card-foreground));
  }

  /* Global text styles - Dynamic colors */
  .text-primary-custom {
    color: hsl(var(--primary)) !important;
  }

  .text-accent-custom {
    color: hsl(var(--accent)) !important;
  }

  /* Global badge styles - Dynamic colors */
  .badge-primary {
