    color: hsl(var(--primary));
    border: 1px solid hsl(var(--primary) / 0.3);
  }

  .badge-success {
    background: hsl(120 100% 25% / 0.2);
    color: hsl(120 100% 45%);
    border: 1px solid hsl(120 100% 45% / 0.3);
  }

  .badge-warning {
    background: hsl(45 100% 25% / 0.2);
    color: hsl(45 100% 45%);
    border: 1px solid hsl(45 100% 45% / 0.3);
  }

  .badge-danger {
    background: hsl(0 100% 25% / 0.2);
    color: hsl(0 100% 45%);
    border: 1px solid hsl(0 100% 45% / 0.3);
  }

  .hacker-table tr:hover {
    background: hsl(var(--primary) / 0.05);
    border-left: 2px solid hsl(var(--primary));
  }

  /* Button styles - Dynamic colors (sin efectos neón) */
  .neon-button {
    background: hsl(0 0% 8%);
    border: 1px solid hsl(var(--primary) / 0.8);
    color: hsl(var(--primary));
  }

  .neon-button:hover {
    background: hsl(var(--primary) / 0.1);
    border-color: hsl(var(--primary));
  }

  /* Terminal-style input fields - Dynamic colors (sin efectos de iluminación) */
  .terminal-input {
    background: hsl(0 0% 6%);
    border: 1px solid hsl(var(--primary) / 0.6);
    color: hsl(var(--foreground));
    font-family: inherit;
  }

  .terminal-input:focus {
    border-color: hsl(var(--primary));
  }

  .terminal-input::placeholder {
    color: hsl(var(--muted-foreground));
  }

  /* Custom badge styles for cybercrime types */
  .badge-hacking { background: hsl(0 80% 25%); color: hsl(0 100% 85%); }
  .badge-phishing { background: hsl(30 80% 25%); color: hsl(30 100% 85%); }
  .badge-malware { background: hsl(270 80% 25%); color: hsl(270 100% 85%); }
  .badge-ransomware { background: hsl(0 0% 25%); color: hsl(0 0% 85%); }
  .badge-fraud { background: hsl(210 80% 25%); color: hsl(210 100% 85%); }
  .badge-identity { background: hsl(60 80% 25%); color: hsl(60 100% 15%); }
  .badge-harassment { background: hsl(320 80% 25%); color: hsl(320 100% 85%); }
  .badge-impersonation { background: hsl(240 80% 25%); color: hsl(240 100% 85%); }

  .badge-pending { background: hsl(60 80% 25%); color: hsl(60 100% 15%); }
  .badge-processing { background: hsl(210 80% 25%); color: hsl(210 100% 85%); }

  /* Dynamic transparency classes */
  .theme-card {
    background: hsl(var(--card) / var(--card-opacity)) !important;
    backdrop-filter: blur(8px);
  }

  .theme-modal {
    background: hsl(var(--card) / var(--modal-opacity)) !important;
    backdrop-filter: blur(12px);
  }

  /* Efectos desactivados - Sin animaciones ni iluminación */
  .badge-completed { background: hsl(120 80% 25%); color: hsl(120 100% 85%); }
  .badge-no-response { background: hsl(0 80% 25%); color: hsl(0 100% 85%); }
  .badge-rejected { background: hsl(0 0% 25%); color: hsl(0 0% 85%); }

  /* Dynamic transparency system */
  .theme-card {
    background: hsl(var(--card) / var(--card-opacity, 0.85));
  }

  .theme-modal {
    background: hsl(var(--card) / var(--modal-opacity, 0.95)) !important;
    backdrop-filter: blur(8px);
  }

  /* Todas las animaciones eliminadas */

  /* Animation toggle */
  .animations-enabled * {
    transition: all 0.3s ease;
  }
}

