import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Save, X } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { insertCyberCaseSchema, type CyberCase } from "@shared/schema";
import { z } from "zod";

const formSchema = insertCyberCaseSchema.extend({
  caseDate: z.string().min(1, "La fecha es requerida"),
  stolenAmount: z.string().min(1, "El monto es requerido"),
});

type FormData = z.infer<typeof formSchema>;

interface CaseModalProps {
  isOpen: boolean;
  onClose: () => void;
  editingCase?: CyberCase | null;
}

const crimeTypes = [
  "Hacking",
  "Phishing", 
  "Malware",
  "Ransomware",
  "Fraude cibernético",
  "Robo de identidad",
  "Ciberacoso",
  "Suplantación de identidad",
];

const investigationStatuses = [
  "Pendiente",
  "En proceso",
  "Completado",
  "Sin respuesta",
  "Rechazado",
];

export function CaseModal({ isOpen, onClose, editingCase }: CaseModalProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const form = useForm<FormData>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      caseDate: editingCase?.caseDate || "",
      expedientNumber: editingCase?.expedientNumber || "",
      crimeType: editingCase?.crimeType || "",
      senderAccountData: editingCase?.senderAccountData || "",
      victim: editingCase?.victim || "",
      receiverAccountData: editingCase?.receiverAccountData || "",
      investigationStatus: editingCase?.investigationStatus || "Pendiente",
      stolenAmount: editingCase?.stolenAmount || "",
      observations: editingCase?.observations || "",
    },
  });

  const createMutation = useMutation({
    mutationFn: async (data: FormData) => {
      const payload = {
        ...data,
        stolenAmount: parseFloat(data.stolenAmount).toString(),
      };
      await apiRequest("POST", "/api/cyber-cases", payload);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/cyber-cases"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard/stats"] });
      toast({
        title: "Éxito",
        description: "Caso creado correctamente",
      });
      handleClose();
    },
    onError: (error) => {
      if (isUnauthorizedError(error as Error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "No se pudo crear el caso",
        variant: "destructive",
      });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async (data: FormData) => {
      const payload = {
        ...data,
        stolenAmount: parseFloat(data.stolenAmount).toString(),
      };
      await apiRequest("PUT", `/api/cyber-cases/${editingCase?.id}`, payload);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/cyber-cases"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard/stats"] });
      toast({
        title: "Éxito",
        description: "Caso actualizado correctamente",
      });
      handleClose();
    },
    onError: (error) => {
      if (isUnauthorizedError(error as Error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "No se pudo actualizar el caso",
        variant: "destructive",
      });
    },
  });

  const handleClose = () => {
    form.reset();
    onClose();
  };

  const onSubmit = (data: FormData) => {
    if (editingCase) {
      updateMutation.mutate(data);
    } else {
      createMutation.mutate(data);
    }
  };

  const isLoading = createMutation.isPending || updateMutation.isPending;

  return (
    <Dialog open={isOpen} onOpenChange={handleClose}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle>
            {editingCase ? "Editar Caso" : "Agregar Nuevo Caso"}
          </DialogTitle>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <FormField
                control={form.control}
                name="caseDate"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>
                      Fecha <span className="text-red-500">*</span>
                    </FormLabel>
                    <FormControl>
                      <Input
                        data-testid="input-case-date"
                        type="date"
                        {...field}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="expedientNumber"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>
                      N° de Expediente <span className="text-red-500">*</span>
                    </FormLabel>
                    <FormControl>
                      <Input
                        data-testid="input-expedient-number"
                        placeholder="EXP-2024-XXXX"
                        {...field}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="crimeType"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>
                    Tipo de Delito <span className="text-red-500">*</span>
                  </FormLabel>
                  <Select onValueChange={field.onChange} value={field.value}>
                    <FormControl>
                      <SelectTrigger data-testid="select-crime-type-modal">
                        <SelectValue placeholder="Seleccionar tipo de delito" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      {crimeTypes.map((type) => (
                        <SelectItem key={type} value={type}>
                          {type}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <FormField
                control={form.control}
                name="senderAccountData"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>
                      Datos de la Cuenta Emisora <span className="text-red-500">*</span>
                    </FormLabel>
                    <FormControl>
                      <Textarea
                        data-testid="textarea-sender-account"
                        placeholder="Banco, número de cuenta, titular, etc."
                        rows={3}
                        className="resize-vertical"
                        {...field}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="receiverAccountData"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>
                      Datos de la Cuenta Receptora <span className="text-red-500">*</span>
                    </FormLabel>
                    <FormControl>
                      <Textarea
                        data-testid="textarea-receiver-account"
                        placeholder="Banco, número de cuenta, titular, etc."
                        rows={3}
                        className="resize-vertical"
                        {...field}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="receiverAccountResearch"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>
                      Pesquisa a Cuenta Receptora
                    </FormLabel>
                    <FormControl>
                      <Textarea
                        data-testid="textarea-receiver-research"
                        placeholder="Resultado de la investigación de la cuenta receptora"
                        rows={3}
                        className="resize-vertical"
                        {...field}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="victim"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>
                    Víctima <span className="text-red-500">*</span>
                  </FormLabel>
                  <FormControl>
                    <Input
                      data-testid="input-victim"
                      placeholder="Nombre completo de la víctima"
                      {...field}
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <FormField
                control={form.control}
                name="investigationStatus"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Pesquisa a Cuenta Receptora</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value}>
                      <FormControl>
                        <SelectTrigger data-testid="select-investigation-status">
                          <SelectValue placeholder="Seleccionar estado" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        {investigationStatuses.map((status) => (
                          <SelectItem key={status} value={status}>
                            {status}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="stolenAmount"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>
                      Monto Sustraído <span className="text-red-500">*</span>
                    </FormLabel>
                    <FormControl>
                      <div className="relative">
                        <span className="absolute left-4 top-3 text-gray-500">$</span>
                        <Input
                          data-testid="input-stolen-amount"
                          type="number"
                          placeholder="0.00"
                          step="0.01"
                          className="pl-8"
                          {...field}
                        />
                      </div>
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="observations"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Observaciones Adicionales</FormLabel>
                  <FormControl>
                    <Textarea
                      data-testid="textarea-observations"
                      placeholder="Detalles adicionales del caso, evidencias, notas, etc."
                      rows={4}
                      className="resize-vertical"
                      {...field}
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="flex justify-end space-x-4 pt-6 border-t border-gray-200">
              <Button
                data-testid="button-cancel"
                type="button"
                variant="outline"
                onClick={handleClose}
                disabled={isLoading}
              >
                <X className="w-4 h-4 mr-2" />
                Cancelar
              </Button>
              <Button
                data-testid="button-save"
                type="submit"
                disabled={isLoading}
              >
                <Save className="w-4 h-4 mr-2" />
                {isLoading 
                  ? "Guardando..." 
                  : editingCase 
                    ? "Actualizar Caso" 
                    : "Guardar Caso"
                }
              </Button>
            </div>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

client/src/Carpeta nueva/Dashboard.tsx.content:
import { useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Skeleton } from "@/components/ui/skeleton";
import { 
  FolderOpen, 
  DollarSign, 
  AlertTriangle, 
  CheckCircle,
  Plus,
  Edit,
  Check
} from "lucide-react";
import { isUnauthorizedError } from "@/lib/authUtils";
import { useToast } from "@/hooks/use-toast";

export function Dashboard() {
  const { toast } = useToast();

  const { data: stats, isLoading, error } = useQuery({
    queryKey: ["/api/dashboard/stats"],
    retry: false,
  });

  useEffect(() => {
    if (error && isUnauthorizedError(error as Error)) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
    }
  }, [error, toast]);

  if (isLoading) {
    return (
      <div className="p-6">
        <div className="mb-8">
          <Skeleton className="h-8 w-64 mb-2" />
          <Skeleton className="h-4 w-96" />
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          {Array.from({ length: 4 }).map((_, i) => (
            <Card key={i}>
              <CardContent className="p-6">
                <Skeleton className="h-12 w-12 rounded-lg mb-4" />
                <Skeleton className="h-4 w-24 mb-2" />
                <Skeleton className="h-8 w-16" />
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-6">
        <div className="text-center py-12">
          <p className="text-red-600">Error al cargar las estadísticas del dashboard</p>
        </div>
      </div>
    );
  }

  const formatCurrency = (amount: string | number) => {
    const num = typeof amount === 'string' ? parseFloat(amount) : amount;
    return new Intl.NumberFormat('es-ES', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(num);
  };

  const statCards = [
    {
      title: "Total de Casos",
      value: stats?.totalCases || 0,
      icon: FolderOpen,
      iconBg: "bg-blue-100",
      iconColor: "text-blue-600",
      change: "+12%",
      changeText: "desde el mes pasado",
      changeColor: "text-green-600",
    },
    {
      title: "Monto Total",
      value: formatCurrency(stats?.totalAmount || 0),
      icon: DollarSign,
      iconBg: "bg-green-100",
      iconColor: "text-green-600",
      change: "+8%",
      changeText: "desde el mes pasado",
      changeColor: "text-green-600",
    },
    {
      title: "Casos Activos",
      value: stats?.activeCases || 0,
      icon: AlertTriangle,
      iconBg: "bg-yellow-100",
      iconColor: "text-yellow-600",
      change: "+3%",
      changeText: "desde la semana pasada",
      changeColor: "text-red-600",
    },
    {
      title: "Casos Resueltos",
      value: stats?.resolvedCases || 0,
      icon: CheckCircle,
      iconBg: "bg-purple-100",
      iconColor: "text-purple-600",
      change: "+15%",
      changeText: "desde el mes pasado",
      changeColor: "text-green-600",
    },
  ];

  const recentActivities = [
    {
      icon: Plus,
      iconBg: "bg-blue-100",
      iconColor: "text-blue-600",
      title: "Nuevo caso agregado #EXP-2024-0156",
      description: "Fraude electrónico - Monto: $45,000",
      time: "Hace 2 horas",
    },
    {
      icon: Check,
      iconBg: "bg-green-100",
      iconColor: "text-green-600",
      title: "Caso resuelto #EXP-2024-0143",
      description: "Estafa bancaria - Recuperado: $12,500",
      time: "Hace 4 horas",
    },
    {
      icon: Edit,
      iconBg: "bg-yellow-100",
      iconColor: "text-yellow-600",
      title: "Actualización de caso #EXP-2024-0134",
      description: "Nueva información de pesquisa agregada",
      time: "Hace 6 horas",
    },
  ];

  return (
    <div className="p-6">
      <div className="mb-8">
        <h2 className="text-2xl font-bold text-gray-800 mb-2">Panel de Control</h2>
        <p className="text-gray-600">Resumen de estadísticas y métricas del sistema</p>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
        {statCards.map((stat, index) => {
          const Icon = stat.icon;
          return (
            <Card key={index}>
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-medium text-gray-600">{stat.title}</p>
                    <p data-testid={`stat-${stat.title.toLowerCase().replace(/\s+/g, '-')}`} className="text-3xl font-bold text-gray-800">
                      {stat.value}
                    </p>
                  </div>
                  <div className={`w-12 h-12 ${stat.iconBg} rounded-lg flex items-center justify-center`}>
                    <Icon className={`${stat.iconColor} text-xl`} size={24} />
                  </div>
                </div>
                <div className="mt-4 flex items-center">
                  <span className={`text-sm font-medium ${stat.changeColor}`}>
                    {stat.change}
                  </span>
                  <span className="text-gray-600 text-sm ml-2">{stat.changeText}</span>
                </div>
              </CardContent>
            </Card>
          );
        })}
      </div>

      {/* Charts Row */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
        <Card>
          <CardHeader>
            <div className="flex items-center justify-between">
              <CardTitle>Casos por Mes</CardTitle>
              <div className="flex items-center space-x-2">
                <span className="w-3 h-3 bg-primary rounded-full"></span>
                <span className="text-sm text-gray-600">2024</span>
              </div>
            </div>
          </CardHeader>
          <CardContent>
            <div className="h-64 flex items-center justify-center">
              <p className="text-gray-500">Gráfico de casos por mes</p>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <div className="flex items-center justify-between">
              <CardTitle>Tipos de Delito</CardTitle>
              <button className="text-sm text-primary hover:underline">Ver todos</button>
            </div>
          </CardHeader>
          <CardContent>
            <div className="h-64">
              {stats?.crimeTypeStats && stats.crimeTypeStats.length > 0 ? (
                <div className="space-y-4">
                  {stats.crimeTypeStats.slice(0, 5).map((crime, index) => (
                    <div key={index} className="flex items-center justify-between">
                      <span className="text-sm text-gray-700">{crime.type}</span>
                      <span className="text-sm font-medium text-gray-900">{crime.count}</span>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="flex items-center justify-center h-full">
                  <p className="text-gray-500">No hay datos de tipos de delito</p>
                </div>
              )}
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Recent Activity */}
      <Card>
        <CardHeader>
          <CardTitle>Actividad Reciente</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {recentActivities.map((activity, index) => {
              const Icon = activity.icon;
              return (
                <div key={index} className="flex items-center space-x-4 p-4 bg-gray-50 rounded-lg">
                  <div className={`w-8 h-8 ${activity.iconBg} rounded-full flex items-center justify-center`}>
                    <Icon className={`${activity.iconColor} text-sm`} size={16} />
                  </div>
                  <div className="flex-1">
                    <p className="font-medium text-gray-800">{activity.title}</p>
                    <p className="text-sm text-gray-600">{activity.description}</p>
                  </div>
                  <span className="text-sm text-gray-500">{activity.time}</span>
                </div>
              );
            })}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

