import { cn } from "@/lib/utils";
import { BarChart3, Database, Settings, Globe, MessageCircle, LogOut, Shield } from "lucide-react";

interface SidebarProps {
  isOpen: boolean;
  onClose: () => void;
  currentSection: string;
  onSectionChange: (section: string) => void;
}

const navigation = [
  { id: "dashboard", name: "Dashboard", icon: BarChart3 },
  { id: "intelligence", name: "Inteligencia", icon: Globe },
  { id: "data-management", name: "Gestión de Casos", icon: Database },
  { id: "chatbot", name: "Asistente Virtual", icon: MessageCircle },
  { id: "settings", name: "Configuración", icon: Settings },
];

export function Sidebar({ isOpen, onClose, currentSection, onSectionChange }: SidebarProps) {
  const handleSectionClick = (sectionId: string) => {
    onSectionChange(sectionId);
    onClose(); // Close sidebar on mobile after selection
  };

  const handleLogout = async () => {
    try {
      const response = await fetch('/api/logout', { 
        method: 'POST',
        credentials: 'include'
      });
      if (response.ok) {
        window.location.href = '/';
      } else {
        window.location.reload();
      }
    } catch (error) {
      console.error('Error al cerrar sesión:', error);
      window.location.href = '/';
    }
  };

  return (
    <div
      className={cn(
        "fixed left-0 top-0 h-full w-64 bg-white shadow-lg transform transition-transform duration-300 z-30",
        isOpen ? "translate-x-0" : "-translate-x-full lg:translate-x-0"
      )}
    >
      <div className="p-6 border-b border-gray-200">
        <div className="flex items-center">
          <div className="w-10 h-10 bg-primary rounded-lg flex items-center justify-center">
            <Shield className="w-6 h-6 text-white" />
          </div>
          <div className="ml-3">
            <h2 className="text-lg font-semibold text-gray-800">Sistema CyberCrime</h2>
            <p className="text-xs text-gray-600">Delitos Informáticos</p>
          </div>
        </div>
      </div>

      <nav className="mt-6">
        <ul className="space-y-1 px-3">
          {navigation.map((item) => {
            const Icon = item.icon;
            const isActive = currentSection === item.id;

            return (
              <li key={item.id}>
                <button
                  data-testid={`nav-${item.id}`}
                  onClick={() => handleSectionClick(item.id)}
                  className={cn(
                    "w-full flex items-center px-4 py-3 text-left rounded-lg transition-colors group",
                    isActive
                      ? "bg-blue-50 text-primary"
                      : "text-gray-700 hover:bg-gray-100 hover:text-gray-900"
                  )}
                >
                  <Icon className={cn(
                    "w-5 h-5",
                    isActive ? "text-primary" : "text-gray-500 group-hover:text-primary"
                  )} />
                  <span className="ml-3">{item.name}</span>
                </button>
              </li>
            );
          })}
        </ul>
      </nav>

      <div className="absolute bottom-0 left-0 right-0 p-4 border-t border-gray-200">
        <button
          data-testid="button-logout"
          onClick={handleLogout}
          className="w-full flex items-center px-4 py-3 text-left rounded-lg hover:bg-red-50 transition-colors group"
        >
          <LogOut className="w-5 h-5 text-gray-500 group-hover:text-red-500" />
          <span className="ml-3 text-gray-700 group-hover:text-red-700">Cerrar Sesión</span>
        </button>
      </div>
    </div>
  );
}

client/src/Carpeta nueva/TopBar.tsx.content:
import { Bell, Menu, User, LogOut } from "lucide-react";
import { useAuth } from "@/hooks/useAuth";

interface TopBarProps {
  onMenuClick: () => void;
  currentSection: string;
}

const sectionTitles = {
  dashboard: "Dashboard",
  "data-management": "Gestión de Datos",
  settings: "Configuración",
  users: "Usuarios",
};

export function TopBar({ onMenuClick, currentSection }: TopBarProps) {
  const { user } = useAuth() as { user: any };

  return (
    <div className="fixed top-0 left-0 lg:left-64 right-0 h-16 bg-white shadow-sm border-b border-gray-200 z-20">
      <div className="flex items-center justify-between h-full px-6">
        <div className="flex items-center">
          <button
            data-testid="button-menu"
            onClick={onMenuClick}
            className="lg:hidden p-2 rounded-md hover:bg-gray-100"
          >
            <Menu className="w-6 h-6 text-gray-600" />
          </button>
          <h1 className="ml-4 text-xl font-semibold text-gray-800">
            {sectionTitles[currentSection as keyof typeof sectionTitles] || "Sistema"}
          </h1>
        </div>

        <div className="flex items-center space-x-4">
          <button 
            data-testid="button-notifications"
            className="p-2 rounded-full hover:bg-gray-100 relative"
          >
            <Bell className="w-5 h-5 text-gray-600" />
            <span className="absolute -top-1 -right-1 w-3 h-3 bg-red-500 rounded-full"></span>
          </button>

          <div className="flex items-center">
            <div className="w-8 h-8 bg-primary rounded-full flex items-center justify-center">
              {user?.profileImageUrl ? (
                <img
                  src={user.profileImageUrl}
                  alt="Profile"
                  className="w-8 h-8 rounded-full object-cover"
                />
              ) : (
                <User className="w-4 h-4 text-white" />
              )}
            </div>
            <div className="ml-3 hidden sm:block">
              <p data-testid="text-username" className="text-sm font-medium text-gray-800">
                {user?.firstName || user?.lastName 
                  ? `${user.firstName || ""} ${user.lastName || ""}`.trim()
                  : user?.email || "Usuario"
                }
              </p>
              <p className="text-xs text-gray-600">Administrador</p>
            </div>
          </div>

          <button
            onClick={async () => {
              await fetch('/api/logout', { method: 'POST' });
              window.location.reload();
            }}
            data-testid="button-logout"
            className="p-2 rounded-full hover:bg-gray-100 text-gray-600 hover:text-gray-800"
          >
            <LogOut className="w-5 h-5" />
          </button>
        </div>
      </div>
    </div>
  );
}

client/src/components/CaseModal.tsx.content:
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Save, X } from "lucide-react";
import { insertCyberCaseSchema, type CyberCase } from "@shared/schema";
import { z } from "zod";

const formSchema = insertCyberCaseSchema.extend({
  caseDate: z.string().min(1, "La fecha es requerida"),
  stolenAmount: z.string().min(1, "El monto es requerido"),
});

type FormData = z.infer<typeof formSchema>;

interface CaseModalProps {
  isOpen: boolean;
  onClose: () => void;
  onSubmit: (data: any) => void;
  initialData?: CyberCase | null;
  isLoading?: boolean;
}

const crimeTypes = [
  "Hacking",
  "Phishing", 
  "Malware",
  "Ransomware",
  "Fraude cibernético",
  "Robo de identidad",
  "Ciberacoso",
  "Suplantación de identidad",
];

const investigationStatuses = [
  "Pendiente",
  "En proceso",
  "Completado",
  "Sin respuesta",
  "Rechazado",
];

export function CaseModal({ isOpen, onClose, onSubmit, initialData, isLoading = false }: CaseModalProps) {
  const form = useForm<FormData>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      caseDate: initialData?.caseDate || "",
      expedientNumber: initialData?.expedientNumber || "",
      crimeType: initialData?.crimeType || "",
      senderAccountData: initialData?.senderAccountData || "",
      victim: initialData?.victim || "",
      receiverAccountData: initialData?.receiverAccountData || "",
      investigationStatus: initialData?.investigationStatus || "Pendiente",
      stolenAmount: initialData?.stolenAmount?.toString() || "",
      observations: initialData?.observations || "",
      receiverAccountResearch: initialData?.receiverAccountResearch || "",
    },
  });

  const handleSubmit = (data: FormData) => {
    const payload = {
      ...data,
      stolenAmount: parseFloat(data.stolenAmount),
    };
    onSubmit(payload);
    form.reset();
  };

  const handleClose = () => {
    form.reset();
    onClose();
  };

  return (
    <Dialog open={isOpen} onOpenChange={handleClose}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto theme-modal">
        <DialogHeader>
          <DialogTitle className="matrix-text font-mono text-xl">
            {initialData ? "EDITAR CASO" : "NUEVO CASO"}
          </DialogTitle>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(handleSubmit)} className="space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="caseDate"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="font-mono text-sm">FECHA DEL CASO</FormLabel>
                    <FormControl>
                      <Input type="date" {...field} className="font-mono" />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="expedientNumber"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="font-mono text-sm">NÚMERO DE EXPEDIENTE</FormLabel>
                    <FormControl>
                      <Input {...field} placeholder="EXP-2025-001" className="font-mono" />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="crimeType"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="font-mono text-sm">TIPO DE DELITO</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value}>
                      <FormControl>
                        <SelectTrigger className="font-mono">
                          <SelectValue placeholder="Selecciona el tipo de delito" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        {crimeTypes.map((type) => (
                          <SelectItem key={type} value={type} className="font-mono">
                            {type}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="victim"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="font-mono text-sm">VÍCTIMA</FormLabel>
                    <FormControl>
                      <Input {...field} placeholder="Nombre de la víctima" className="font-mono" />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="stolenAmount"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="font-mono text-sm">MONTO ESTAFADO</FormLabel>
                    <FormControl>
                      <Input
                        {...field}
                        type="number"
                        step="0.01"
                        placeholder="0.00"
                        className="font-mono"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="investigationStatus"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="font-mono text-sm">ESTADO DE INVESTIGACIÓN</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value}>
                      <FormControl>
                        <SelectTrigger className="font-mono">
                          <SelectValue placeholder="Selecciona el estado" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        {investigationStatuses.map((status) => (
                          <SelectItem key={status} value={status} className="font-mono">
                            {status}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="senderAccountData"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="font-mono text-sm">DATOS DE LA CUENTA EMISORA</FormLabel>
                  <FormControl>
                    <Textarea
                      {...field}
                      placeholder="Información de la cuenta desde donde se envió el dinero..."
                      className="font-mono min-h-[80px]"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="receiverAccountData"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="font-mono text-sm">DATOS DE LA CUENTA RECEPTORA</FormLabel>
                  <FormControl>
                    <Textarea
                      {...field}
                      placeholder="Información de la cuenta donde se recibió el dinero..."
                      className="font-mono min-h-[80px]"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="observations"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="font-mono text-sm">OBSERVACIONES</FormLabel>
                  <FormControl>
                    <Textarea
                      {...field}
                      placeholder="Observaciones adicionales del caso..."
                      className="font-mono min-h-[100px]"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="flex justify-end space-x-4 pt-4 border-t border-border/20">
              <Button
                type="button"
                variant="outline"
                onClick={handleClose}
                className="font-mono"
                disabled={isLoading}
              >
                <X className="w-4 h-4 mr-2" />
                CANCELAR
              </Button>
              <Button
                type="submit"
                className="font-mono neon-border"
                disabled={isLoading}
              >
                <Save className="w-4 h-4 mr-2" />
                {isLoading ? "GUARDANDO..." : "GUARDAR"}
              </Button>
            </div>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/ChatbotAdministrat.content:
import { useState } from "react";
import { Bot, Settings, MessageSquare, TrendingUp, RefreshCw, Save } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Textarea } from "@/components/ui/textarea";
import { Switch } from "@/components/ui/switch";
import { Label } from "@/components/ui/label";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { useToast } from "@/hooks/use-toast";

interface ChatbotConfig {
  isActive: boolean;
  welcomeMessage: string;
  maxResponseLength: number;
  responseDelay: number;
  enableLearning: boolean;
  logConversations: boolean;
}

interface Conversation {
  id: string;
  usuario: string;
  mensaje: string;
  respuesta: string;
  fecha: string;
  calificacion?: number;
}

const mockConversations: Conversation[] = [
  {
    id: "1",
    usuario: "María González",
    mensaje: "¿Cómo analizar un caso de phishing?",
    respuesta: "El phishing es una técnica de ingeniería social donde los atacantes se hacen pasar por entidades confiables...",
    fecha: "2025-01-16 22:44",
    calificacion: 5
  },
  {
    id: "2",
    usuario: "Carlos Rodríguez",
    mensaje: "Información sobre malware bancario",
    respuesta: "Para casos de malware en delitos informáticos: 1. Aísla el sistema infectado 2. Captura evidencia forense...",
    fecha: "2025-01-16 22:45",
    calificacion: 4
  },
  {
    id: "3",
    usuario: "Admin Sistema",
    mensaje: "Hola",
    respuesta: "Hola, soy tu asistente especializado en delitos informáticos. He recibido tu consulta...",
    fecha: "2025-01-16 22:45"
  }
];

export function ChatbotAdministration() {
  const [config, setConfig] = useState<ChatbotConfig>({
    isActive: true,
    welcomeMessage: "¡Hola! Soy tu asistente especializado en delitos informáticos. Puedo ayudarte con información sobre investigaciones, técnicas de análisis, normativas legales y más. ¿En qué puedo asistirte?",
    maxResponseLength: 500,
    responseDelay: 1000,
    enableLearning: true,
    logConversations: true
  });

  const [conversations, setConversations] = useState<Conversation[]>(mockConversations);
  const [newResponse, setNewResponse] = useState("");
  const [newKeyword, setNewKeyword] = useState("");
  const { toast } = useToast();

  const handleSaveConfig = () => {
    // Aquí se guardaría la configuración en el backend
    toast({
      title: "Configuración guardada",
      description: "Los cambios en el chatbot han sido aplicados exitosamente",
    });
  };

  const handleAddResponse = () => {
    if (!newKeyword || !newResponse) {
      toast({
        title: "Error",
        description: "Palabra clave y respuesta son obligatorias",
        variant: "destructive",
      });
      return;
    }

    // Simular agregado de nueva respuesta
    toast({
      title: "Respuesta agregada",
      description: `Nueva respuesta agregada para "${newKeyword}"`,
    });

    setNewKeyword("");
    setNewResponse("");
  };

  const stats = {
    totalConversations: conversations.length,
    averageRating: conversations.filter(c => c.calificacion).reduce((acc, c) => acc + (c.calificacion || 0), 0) / conversations.filter(c => c.calificacion).length,
    responseRate: 100, // Porcentaje de respuestas exitosas
    activeUsers: new Set(conversations.map(c => c.usuario)).size
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-3xl font-bold matrix-text font-mono">ADMINISTRACIÓN DE CHATBOT</h2>
          <p className="text-muted-foreground font-mono">Configuración y monitoreo del asistente virtual</p>
        </div>
        <Button onClick={handleSaveConfig} className="neon-border font-mono">
          <Save className="w-4 h-4 mr-2" />
          GUARDAR CONFIGURACIÓN
        </Button>
      </div>

      {/* Estadísticas */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card className="neon-border theme-card">
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-mono flex items-center">
              <MessageSquare className="w-4 h-4 mr-2" />
              CONVERSACIONES
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold matrix-text">{stats.totalConversations}</div>
          </CardContent>
        </Card>
        <Card className="neon-border theme-card">
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-mono flex items-center">
              <TrendingUp className="w-4 h-4 mr-2" />
              CALIFICACIÓN
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-green-500">
              {stats.averageRating.toFixed(1)}/5
            </div>
          </CardContent>
        </Card>
        <Card className="neon-border theme-card">
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-mono">TASA RESPUESTA</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-blue-500">{stats.responseRate}%</div>
          </CardContent>
        </Card>
        <Card className="neon-border theme-card">
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-mono">USUARIOS ACTIVOS</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-purple-500">{stats.activeUsers}</div>
          </CardContent>
        </Card>
      </div>

      <Tabs defaultValue="config" className="space-y-4">
        <TabsList className="grid w-full grid-cols-3">
          <TabsTrigger value="config" className="font-mono">CONFIGURACIÓN</TabsTrigger>
          <TabsTrigger value="responses" className="font-mono">RESPUESTAS</TabsTrigger>
          <TabsTrigger value="logs" className="font-mono">CONVERSACIONES</TabsTrigger>
        </TabsList>

        <TabsContent value="config">
          <Card className="neon-border theme-card">
            <CardHeader>
              <CardTitle className="flex items-center font-mono">
                <Settings className="w-5 h-5 mr-2" />
                CONFIGURACIÓN DEL CHATBOT
              </CardTitle>
              <CardDescription>
                Ajusta el comportamiento y parámetros del asistente virtual
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-6">
              <div className="flex items-center space-x-2">
                <Switch
                  id="active"
                  checked={config.isActive}
                  onCheckedChange={(checked) => setConfig({...config, isActive: checked})}
                />
                <Label htmlFor="active" className="font-mono">CHATBOT ACTIVO</Label>
              </div>

              <div className="space-y-2">
                <Label htmlFor="welcome" className="font-mono">MENSAJE DE BIENVENIDA</Label>
                <Textarea
                  id="welcome"
                  value={config.welcomeMessage}
                  onChange={(e) => setConfig({...config, welcomeMessage: e.target.value})}
                  className="min-h-20"
                />
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="maxLength" className="font-mono">LONGITUD MÁXIMA RESPUESTA</Label>
                  <Input
                    id="maxLength"
                    type="number"
                    value={config.maxResponseLength}
                    onChange={(e) => setConfig({...config, maxResponseLength: parseInt(e.target.value)})}
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="delay" className="font-mono">RETRASO RESPUESTA (ms)</Label>
                  <Input
                    id="delay"
                    type="number"
                    value={config.responseDelay}
                    onChange={(e) => setConfig({...config, responseDelay: parseInt(e.target.value)})}
                  />
                </div>
              </div>

              <div className="flex items-center space-x-2">
                <Switch
                  id="learning"
                  checked={config.enableLearning}
                  onCheckedChange={(checked) => setConfig({...config, enableLearning: checked})}
                />
                <Label htmlFor="learning" className="font-mono">HABILITAR APRENDIZAJE</Label>
              </div>

              <div className="flex items-center space-x-2">
                <Switch
                  id="logging"
                  checked={config.logConversations}
                  onCheckedChange={(checked) => setConfig({...config, logConversations: checked})}
                />
                <Label htmlFor="logging" className="font-mono">REGISTRAR CONVERSACIONES</Label>
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="responses">
          <Card className="neon-border theme-card">
            <CardHeader>
              <CardTitle className="font-mono">GESTIÓN DE RESPUESTAS</CardTitle>
              <CardDescription>
                Agrega y modifica respuestas automáticas del chatbot
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="keyword" className="font-mono">PALABRA CLAVE</Label>
                  <Input
                    id="keyword"
                    value={newKeyword}
                    onChange={(e) => setNewKeyword(e.target.value)}
                    placeholder="Ej: ransomware, crypto, etc."
                  />
                </div>
                <div className="space-y-2">
                  <Label className="font-mono">RESPUESTA</Label>
                  <Textarea
                    value={newResponse}
                    onChange={(e) => setNewResponse(e.target.value)}
                    placeholder="Respuesta que dará el chatbot..."
                    className="min-h-20"
                  />
                </div>
              </div>
              <Button onClick={handleAddResponse} className="font-mono">
                AGREGAR RESPUESTA
              </Button>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="logs">
          <Card className="neon-border theme-card">
            <CardHeader>
              <CardTitle className="flex items-center font-mono">
                <Bot className="w-5 h-5 mr-2" />
                REGISTRO DE CONVERSACIONES
              </CardTitle>
              <CardDescription>
                Historial de interacciones con el chatbot
              </CardDescription>
            </CardHeader>
            <CardContent>
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead className="font-mono">USUARIO</TableHead>
                    <TableHead className="font-mono">MENSAJE</TableHead>
                    <TableHead className="font-mono">RESPUESTA</TableHead>
                    <TableHead className="font-mono">FECHA</TableHead>
                    <TableHead className="font-mono">CALIFICACIÓN</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {conversations.map((conv) => (
                    <TableRow key={conv.id}>
                      <TableCell className="font-mono">{conv.usuario}</TableCell>
                      <TableCell className="font-mono max-w-xs truncate">{conv.mensaje}</TableCell>
                      <TableCell className="font-mono max-w-xs truncate">{conv.respuesta}</TableCell>
                      <TableCell className="font-mono">{conv.fecha}</TableCell>
                      <TableCell className="font-mono">
                        {conv.calificacion ? `${conv.calificacion}/5` : "-"}
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

