import { useEffect, useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Skeleton } from "@/components/ui/skeleton";
import { 
  FolderOpen, 
  DollarSign, 
  AlertTriangle, 
  CheckCircle,
  Plus,
  Edit,
  Check,
  Users,
  FileText,
  Bot,
  ArrowLeft
} from "lucide-react";
import { isUnauthorizedError } from "@/lib/authUtils";
import { useToast } from "@/hooks/use-toast";
import { UserAdministration } from "./UserAdministration";
import { ReportAdministration } from "./ReportAdministration";
import { ChatbotAdministration } from "./ChatbotAdministration";

export function Dashboard() {
  const [currentView, setCurrentView] = useState<"main" | "users" | "reports" | "chatbot">("main");
  const { toast } = useToast();

  const { data: stats, isLoading, error } = useQuery<{
    totalCases: number;
    totalAmount: string;
    activeCases: number;
    resolvedCases: number;
    crimeTypeStats: { type: string; count: number }[];
  }>({
    queryKey: ["/api/dashboard/stats"],
    retry: false,
  });

  useEffect(() => {
    if (error && isUnauthorizedError(error as Error)) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
    }
  }, [error, toast]);

  if (isLoading) {
    return (
      <div className="p-6 bg-background min-h-screen">
        <div className="mb-8">
          <Skeleton className="h-8 w-64 mb-2" />
          <Skeleton className="h-4 w-96" />
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          {Array.from({ length: 4 }).map((_, i) => (
            <Card key={i} className="neon-border">
              <CardContent className="p-6">
                <Skeleton className="h-12 w-12 rounded-lg mb-4" />
                <Skeleton className="h-4 w-24 mb-2" />
                <Skeleton className="h-8 w-16" />
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-6 bg-background min-h-screen">
        <div className="text-center py-12">
          <p className="text-destructive">Error al cargar las estadísticas del dashboard</p>
        </div>
      </div>
    );
  }

  const formatCurrency = (amount: string | number) => {
    const num = typeof amount === 'string' ? parseFloat(amount) : amount;
    return new Intl.NumberFormat('es-ES', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(num);
  };

  const statCards = [
    {
      title: "TOTAL DE CASOS",
      value: stats?.totalCases || 0,
      icon: FolderOpen,
      iconColor: "text-primary",
      change: "+12%",
      changeText: "desde el mes pasado",
      changeColor: "text-accent",
    },
    {
      title: "MONTO TOTAL",
      value: formatCurrency(stats?.totalAmount || 0),
      icon: DollarSign,
      iconColor: "text-accent",
      change: "+8%",
      changeText: "desde el mes pasado",
      changeColor: "text-accent",
    },
    {
      title: "CASOS ACTIVOS",
      value: stats?.activeCases || 0,
      icon: AlertTriangle,
      iconColor: "text-destructive",
      change: "+3%",
      changeText: "desde la semana pasada",
      changeColor: "text-destructive",
    },
    {
      title: "CASOS RESUELTOS",
      value: stats?.resolvedCases || 0,
      icon: CheckCircle,
      iconColor: "text-primary",
      change: "+15%",
      changeText: "desde el mes pasado",
      changeColor: "text-accent",
    },
  ];

  const recentActivities = [
    {
      icon: Plus,
      iconColor: "text-primary",
      title: "NUEVO CASO AGREGADO #EXP-2024-0156",
      description: "FRAUDE ELECTRÓNICO - MONTO: $45,000",
      time: "HACE 2 HORAS",
    },
    {
      icon: Check,
      iconColor: "text-accent",
      title: "CASO RESUELTO #EXP-2024-0143",
      description: "ESTAFA BANCARIA - RECUPERADO: $12,500",
      time: "HACE 4 HORAS",
    },
    {
      icon: Edit,
      iconColor: "text-muted-foreground",
      title: "ACTUALIZACIÓN DE CASO #EXP-2024-0134",
      description: "NUEVA INFORMACIÓN DE PESQUISA AGREGADA",
      time: "HACE 6 HORAS",
    },
  ];

  // Renderizar las diferentes vistas
  if (currentView === "users") {
    return (
      <div className="p-6 bg-background min-h-screen">
        <div className="mb-6">
          <Button
            onClick={() => setCurrentView("main")}
            variant="outline"
            className="font-mono neon-border"
          >
            <ArrowLeft className="w-4 h-4 mr-2" />
            VOLVER AL DASHBOARD
          </Button>
        </div>
        <UserAdministration />
      </div>
    );
  }

  if (currentView === "reports") {
    return (
      <div className="p-6 bg-background min-h-screen">
        <div className="mb-6">
          <Button
            onClick={() => setCurrentView("main")}
            variant="outline"
            className="font-mono neon-border"
          >
            <ArrowLeft className="w-4 h-4 mr-2" />
            VOLVER AL DASHBOARD
          </Button>
        </div>
        <ReportAdministration />
      </div>
    );
  }

  if (currentView === "chatbot") {
    return (
      <div className="p-6 bg-background min-h-screen">
        <div className="mb-6">
          <Button
            onClick={() => setCurrentView("main")}
            variant="outline"
            className="font-mono neon-border"
          >
            <ArrowLeft className="w-4 h-4 mr-2" />
            VOLVER AL DASHBOARD
          </Button>
        </div>
        <ChatbotAdministration />
      </div>
    );
  }

  return (
    <div className="p-6 bg-background min-h-screen">
      <div className="mb-8">
        <h1 className="text-3xl font-bold matrix-text mb-2">DASHBOARD DE CONTROL</h1>
        <p className="text-muted-foreground font-mono">Sistema de gestión de delitos informáticos</p>
      </div>

      {/* Botones de acceso a secciones administrativas */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-8">
        <Card className="neon-border theme-card hover:bg-primary/5 transition-colors cursor-pointer" onClick={() => setCurrentView("users")}>
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <h3 className="text-lg font-bold matrix-text font-mono">ADMINISTRACIÓN</h3>
                <p className="text-sm text-muted-foreground font-mono">DE USUARIOS</p>
              </div>
              <Users className="w-8 h-8 text-primary" />
            </div>
          </CardContent>
        </Card>

        <Card className="neon-border theme-card hover:bg-primary/5 transition-colors cursor-pointer" onClick={() => setCurrentView("reports")}>
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <h3 className="text-lg font-bold matrix-text font-mono">ADMINISTRACIÓN</h3>
                <p className="text-sm text-muted-foreground font-mono">DE PLANILLAS</p>
              </div>
              <FileText className="w-8 h-8 text-primary" />
            </div>
          </CardContent>
        </Card>

        <Card className="neon-border theme-card hover:bg-primary/5 transition-colors cursor-pointer" onClick={() => setCurrentView("chatbot")}>
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <h3 className="text-lg font-bold matrix-text font-mono">ADMINISTRACIÓN</h3>
                <p className="text-sm text-muted-foreground font-mono">DE CHATBOT</p>
              </div>
              <Bot className="w-8 h-8 text-primary" />
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
        {statCards.map((stat, index) => {
          const Icon = stat.icon;
          return (
            <Card key={index} className="neon-border theme-card">
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-medium text-muted-foreground font-mono">{stat.title}</p>
                    <p data-testid={`stat-${stat.title.toLowerCase().replace(/\s+/g, '-')}`} className="text-3xl font-bold text-foreground font-mono">
                      {stat.value}
                    </p>
                  </div>
                  <div className="w-12 h-12 bg-secondary rounded-lg flex items-center justify-center neon-border">
                    <Icon className={`${stat.iconColor} text-xl`} size={24} />
                  </div>
                </div>
                <div className="mt-4 flex items-center">
                  <span className={`text-sm font-medium font-mono ${stat.changeColor}`}>
                    {stat.change}
                  </span>
                  <span className="text-muted-foreground text-sm ml-2 font-mono">{stat.changeText}</span>
                </div>
              </CardContent>
            </Card>
          );
        })}
      </div>

      {/* Charts Row */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
        <Card className="neon-border theme-card">
          <CardHeader>
            <div className="flex items-center justify-between">
              <CardTitle className="matrix-text font-mono">[ CASOS POR MES ]</CardTitle>
              <div className="flex items-center space-x-2">
                <span className="w-3 h-3 bg-primary rounded-full"></span>
                <span className="text-sm text-muted-foreground font-mono">2024</span>
              </div>
            </div>
          </CardHeader>
          <CardContent>
            <div className="h-64 flex items-center justify-center">
              <p className="text-muted-foreground font-mono">&gt;&gt; GRÁFICO DE CASOS POR MES &lt;&lt;</p>
            </div>
          </CardContent>
        </Card>

        <Card className="neon-border theme-card">
          <CardHeader>
            <div className="flex items-center justify-between">
              <CardTitle className="matrix-text font-mono">[ TIPOS DE DELITO ]</CardTitle>
              <button className="text-sm text-primary hover:text-primary/80 font-mono">VER TODOS</button>
            </div>
          </CardHeader>
          <CardContent>
            <div className="h-64">
              {stats?.crimeTypeStats && stats.crimeTypeStats.length > 0 ? (
                <div className="space-y-4">
                  {stats.crimeTypeStats.slice(0, 5).map((crime, index) => (
                    <div key={index} className="flex items-center justify-between p-2 rounded border border-border/50">
                      <span className="text-sm text-foreground font-mono">{crime.type}</span>
                      <span className="text-sm font-medium text-primary font-mono">[{crime.count}]</span>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="flex items-center justify-center h-full">
                  <p className="text-muted-foreground font-mono">&gt;&gt; NO HAY DATOS DISPONIBLES &lt;&lt;</p>
                </div>
              )}
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Recent Activity */}
      <Card className="neon-border theme-card">
        <CardHeader>
          <CardTitle className="matrix-text font-mono">[ ACTIVIDAD RECIENTE ]</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {recentActivities.map((activity, index) => {
              const Icon = activity.icon;
              return (
                <div key={index} className="flex items-start space-x-4 p-4 rounded border border-border/50 hover:bg-primary/5 transition-colors">
                  <div className="w-10 h-10 bg-secondary rounded-lg flex items-center justify-center neon-border">
                    <Icon className={`${activity.iconColor}`} size={20} />
                  </div>
                  <div className="flex-1 min-w-0">
                    <p className="text-sm font-medium text-foreground font-mono">{activity.title}</p>
                    <p className="text-sm text-muted-foreground font-mono">{activity.description}</p>
                    <p className="text-xs text-muted-foreground font-mono mt-1">{activity.time}</p>
                  </div>
                </div>
              );
            })}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/DataManagement.tsx.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Skeleton } from "@/components/ui/skeleton";
import { Badge } from "@/components/ui/badge";
import { 
  Plus, 
  Search, 
  X, 
  Download, 
  Edit, 
  Trash2,
  ChevronLeft,
  ChevronRight
} from "lucide-react";
import { CaseModal } from "./CaseModal";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import type { CyberCase } from "@shared/schema";

interface CasesResponse {
  cases: CyberCase[];
  total: number;
}

export function DataManagement() {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editingCase, setEditingCase] = useState<CyberCase | null>(null);
  const [filters, setFilters] = useState({
    search: "",
    crimeType: "",
    dateFrom: "",
    dateTo: "",
    page: 1,
    limit: 10,
  });

  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: casesData, isLoading, error } = useQuery<CasesResponse>({
    queryKey: ["/api/cyber-cases", filters],
    retry: false,
  });

  useEffect(() => {
    if (error && isUnauthorizedError(error as Error)) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
    }
  }, [error, toast]);

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest("DELETE", `/api/cyber-cases/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/cyber-cases"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard/stats"] });
      toast({
        title: "Éxito",
        description: "Caso eliminado correctamente",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error as Error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "No se pudo eliminar el caso",
        variant: "destructive",
      });
    },
  });

  const handleFilterChange = (key: string, value: string) => {
    setFilters(prev => ({
      ...prev,
      [key]: value,
      page: 1, // Reset to first page when filtering
    }));
  };

  const handleClearFilters = () => {
    setFilters({
      search: "",
      crimeType: "",
      dateFrom: "",
      dateTo: "",
      page: 1,
      limit: 10,
    });
  };

  const handleEditCase = (caseData: CyberCase) => {
    setEditingCase(caseData);
    setIsModalOpen(true);
  };

  const handleDeleteCase = (id: string) => {
    if (window.confirm("¿Está seguro de que desea eliminar este caso?")) {
      deleteMutation.mutate(id);
    }
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setEditingCase(null);
  };

  const getCrimeTypeBadgeColor = (type: string) => {
    const colors = {
      "Hacking": "bg-red-100 text-red-800",
      "Phishing": "bg-orange-100 text-orange-800",
      "Malware": "bg-purple-100 text-purple-800",
      "Ransomware": "bg-gray-100 text-gray-800",
      "Fraude cibernético": "bg-blue-100 text-blue-800",
      "Robo de identidad": "bg-yellow-100 text-yellow-800",
      "Ciberacoso": "bg-pink-100 text-pink-800",
      "Suplantación de identidad": "bg-indigo-100 text-indigo-800",
    };
    return colors[type as keyof typeof colors] || "bg-gray-100 text-gray-800";
  };

  const getInvestigationStatusBadge = (status: string) => {
    const statusMap = {
      "Pendiente": "bg-yellow-100 text-yellow-800",
      "En proceso": "bg-blue-100 text-blue-800",
      "Completado": "bg-green-100 text-green-800",
      "Sin respuesta": "bg-red-100 text-red-800",
      "Rechazado": "bg-gray-100 text-gray-800",
    };
    return statusMap[status as keyof typeof statusMap] || "bg-gray-100 text-gray-800";
  };

  const formatCurrency = (amount: string) => {
    const num = parseFloat(amount);
    return new Intl.NumberFormat('es-ES', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(num);
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('es-ES');
  };

  const totalPages = Math.ceil((casesData?.total || 0) / filters.limit);

  if (isLoading) {
    return (
      <div className="p-6">
        <div className="mb-8">
          <Skeleton className="h-8 w-64 mb-2" />
          <Skeleton className="h-4 w-96" />
        </div>
        <div className="space-y-6">
          <Card>
            <CardContent className="p-6">
              <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                {Array.from({ length: 4 }).map((_, i) => (
                  <Skeleton key={i} className="h-10" />
                ))}
              </div>
            </CardContent>
          </Card>
          <Card>
            <CardContent className="p-0">
              <div className="space-y-4 p-6">
                {Array.from({ length: 5 }).map((_, i) => (
                  <Skeleton key={i} className="h-16 w-full" />
                ))}
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-6">
        <div className="text-center py-12">
          <p className="text-red-600">Error al cargar los datos</p>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6">
      <div className="mb-8 flex flex-col sm:flex-row sm:items-center sm:justify-between">
        <div>
          <h2 className="text-2xl font-bold text-gray-800 mb-2">Gestión de Datos</h2>
          <p className="text-gray-600">Administre todos los casos de delitos informáticos</p>
        </div>
        <Button
          data-testid="button-new-case"
          onClick={() => setIsModalOpen(true)}
          className="mt-4 sm:mt-0"
        >
          <Plus className="w-4 h-4 mr-2" />
          Nuevos Datos
        </Button>
      </div>

      {/* Filters */}
      <Card className="mb-6">
        <CardContent className="p-6">
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            <div>
              <Label htmlFor="search">Buscar</Label>
              <div className="relative">
                <Input
                  id="search"
                  data-testid="input-search"
                  placeholder="Buscar por expediente, delito..."
                  value={filters.search}
                  onChange={(e) => handleFilterChange("search", e.target.value)}
                  className="pl-10"
                />
                <Search className="w-4 h-4 absolute left-3 top-3 text-gray-400" />
              </div>
            </div>

            <div>
              <Label htmlFor="crimeType">Tipo de Delito</Label>
              <Select
                value={filters.crimeType}
                onValueChange={(value) => handleFilterChange("crimeType", value)}
              >
                <SelectTrigger data-testid="select-crime-type">
                  <SelectValue placeholder="Todos los delitos" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="">Todos los delitos</SelectItem>
                  <SelectItem value="Hacking">Hacking</SelectItem>
                  <SelectItem value="Phishing">Phishing</SelectItem>
                  <SelectItem value="Malware">Malware</SelectItem>
                  <SelectItem value="Ransomware">Ransomware</SelectItem>
                  <SelectItem value="Fraude cibernético">Fraude cibernético</SelectItem>
                  <SelectItem value="Robo de identidad">Robo de identidad</SelectItem>
                  <SelectItem value="Ciberacoso">Ciberacoso</SelectItem>
                  <SelectItem value="Suplantación de identidad">Suplantación de identidad</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div>
              <Label htmlFor="dateFrom">Fecha Desde</Label>
              <Input
                id="dateFrom"
                data-testid="input-date-from"
                type="date"
                value={filters.dateFrom}
                onChange={(e) => handleFilterChange("dateFrom", e.target.value)}
              />
            </div>

            <div>
              <Label htmlFor="dateTo">Fecha Hasta</Label>
              <Input
                id="dateTo"
                data-testid="input-date-to"
                type="date"
                value={filters.dateTo}
                onChange={(e) => handleFilterChange("dateTo", e.target.value)}
              />
            </div>
          </div>

          <div className="mt-4 flex flex-wrap gap-2">
            <Button data-testid="button-clear" variant="outline" onClick={handleClearFilters}>
              <X className="w-4 h-4 mr-2" />
              Limpiar Filtros
            </Button>
            <Button data-testid="button-export" variant="outline">
              <Download className="w-4 h-4 mr-2" />
              Exportar
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Data Table */}
      <Card>
        <CardContent className="p-0">
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead className="bg-gray-50 border-b border-gray-200">
                <tr>
                  <th className="px-6 py-4 text-left text-xs font-semibold text-gray-600 uppercase tracking-wider">
                    Fecha
                  </th>
                  <th className="px-6 py-4 text-left text-xs font-semibold text-gray-600 uppercase tracking-wider">
                    N° de Expediente
                  </th>
                  <th className="px-6 py-4 text-left text-xs font-semibold text-gray-600 uppercase tracking-wider">
                    Delito
                  </th>
                  <th className="px-6 py-4 text-left text-xs font-semibold text-gray-600 uppercase tracking-wider">
                    Víctima
                  </th>
                  <th className="px-6 py-4 text-left text-xs font-semibold text-gray-600 uppercase tracking-wider">
                    Estado
                  </th>
                  <th className="px-6 py-4 text-left text-xs font-semibold text-gray-600 uppercase tracking-wider">
                    Monto Sustraído
                  </th>
                  <th className="px-6 py-4 text-left text-xs font-semibold text-gray-600 uppercase tracking-wider">
                    Acciones
                  </th>
                </tr>
              </thead>
              <tbody className="divide-y divide-gray-200">
                {!casesData?.cases || casesData.cases.length === 0 ? (
                  <tr>
                    <td colSpan={7} className="px-6 py-12 text-center text-gray-500">
                      No se encontraron casos que coincidan con los filtros
                    </td>
                  </tr>
                ) : (
                  casesData.cases.map((caseItem: CyberCase) => (
                    <tr key={caseItem.id} className="hover:bg-gray-50 transition-colors">
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {formatDate(caseItem.caseDate)}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <span className="text-sm font-medium text-primary">
                          {caseItem.expedientNumber}
                        </span>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <Badge className={getCrimeTypeBadgeColor(caseItem.crimeType)}>
                          {caseItem.crimeType}
                        </Badge>
                      </td>
                      <td className="px-6 py-4 text-sm text-gray-900 max-w-xs truncate">{caseItem.victim}</td>
                      <td className="px-6 py-4 text-sm text-gray-900">
                        <Badge className={getInvestigationStatusBadge(caseItem.investigationStatus)}>
                          {caseItem.investigationStatus}
                        </Badge>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                        {formatCurrency(caseItem.stolenAmount)}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-right">
                        <div className="flex space-x-1">
                          <Button
                            data-testid={`button-edit-${caseItem.id}`}
                            variant="ghost"
                            size="sm"
                            onClick={() => handleEditCase(caseItem)}
                          >
                            <Edit className="w-4 h-4" />
                          </Button>
                          <Button
                            data-testid={`button-delete-${caseItem.id}`}
                            variant="ghost"
                            size="sm"
                            onClick={() => handleDeleteCase(caseItem.id)}
                            disabled={deleteMutation.isPending}
                            className="text-red-600 hover:text-red-700"
                          >
                            <Trash2 className="w-4 h-4" />
                          </Button>
                        </div>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>

          {/* Pagination */}
          {(casesData?.total ?? 0) > 0 && (
            <div className="bg-gray-50 px-6 py-4 border-t border-gray-200 flex items-center justify-between">
              <div className="flex items-center text-sm text-gray-600">
                Mostrando{" "}
                <span className="font-medium text-gray-900 mx-1">
                  {((filters.page - 1) * filters.limit) + 1}-{Math.min(filters.page * filters.limit, casesData?.total || 0)}
                </span>{" "}
                de{" "}
                <span className="font-medium text-gray-900 ml-1">{casesData?.total || 0}</span>{" "}
                resultados
              </div>
              <div className="flex items-center space-x-2">
                <Button
                  data-testid="button-prev-page"
                  variant="outline"
                  size="sm"
                  onClick={() => setFilters(prev => ({ ...prev, page: prev.page - 1 }))}
                  disabled={filters.page <= 1}
                >
                  <ChevronLeft className="w-4 h-4" />
                  Anterior
                </Button>
                <span className="text-sm text-gray-600">
                  Página {filters.page} de {totalPages}
                </span>
                <Button
                  data-testid="button-next-page"
                  variant="outline"
                  size="sm"
                  onClick={() => setFilters(prev => ({ ...prev, page: prev.page + 1 }))}
                  disabled={filters.page >= totalPages}
                >
                  Siguiente
                  <ChevronRight className="w-4 h-4" />
                </Button>
              </div>
            </div>
          )}
        </CardContent>
      </Card>

      <CaseModal
        isOpen={isModalOpen}
        onClose={handleCloseModal}
        editingCase={editingCase}
      />
    </div>
  );
}

client/src/components/DataManagementFast.content:
import { useState, useMemo, useCallback, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Skeleton } from "@/components/ui/skeleton";
import { Badge } from "@/components/ui/badge";
import { 
  Plus, 
  Search, 
  Edit, 
  Trash2,
  ChevronLeft,
  ChevronRight,
  Eye
} from "lucide-react";
import { CaseModal } from "./CaseModal";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import type { CyberCase } from "@shared/schema";

interface CasesResponse {
  cases: CyberCase[];
  total: number;
  page: number;
  totalPages: number;
}

// Use debounced search to reduce API calls
function useDebounce<T>(value: T, delay: number): T {
  const [debouncedValue, setDebouncedValue] = useState<T>(value);

  useEffect(() => {
    const handler = setTimeout(() => {
      setDebouncedValue(value);
    }, delay);

    return () => {
      clearTimeout(handler);
    };
  }, [value, delay]);

  return debouncedValue;
}

const CRIME_TYPES = [
  "Hacking", "Phishing", "Malware", "Ransomware", 
  "Fraude cibernético", "Robo de identidad", "Ciberacoso", "Suplantación de identidad"
];

const BADGE_COLORS = {
  crimeType: {
    "Hacking": "bg-red-500/20 text-red-400 border-red-500/30",
    "Phishing": "bg-orange-500/20 text-orange-400 border-orange-500/30",
    "Malware": "bg-purple-500/20 text-purple-400 border-purple-500/30",
    "Ransomware": "bg-pink-500/20 text-pink-400 border-pink-500/30",
    "Fraude cibernético": "bg-yellow-500/20 text-yellow-400 border-yellow-500/30",
    "Robo de identidad": "bg-blue-500/20 text-blue-400 border-blue-500/30",
    "Ciberacoso": "bg-cyan-500/20 text-cyan-400 border-cyan-500/30",
    "Suplantación de identidad": "bg-green-500/20 text-green-400 border-green-500/30",
  },
  status: {
    "Pendiente": "bg-yellow-500/20 text-yellow-400 border-yellow-500/30",
    "En proceso": "bg-blue-500/20 text-blue-400 border-blue-500/30", 
    "Completado": "bg-green-500/20 text-green-400 border-green-500/30",
    "Sin respuesta": "bg-gray-500/20 text-gray-400 border-gray-500/30",
    "Rechazado": "bg-red-500/20 text-red-400 border-red-500/30",
  }
} as const;

export function DataManagementFast() {
  const [modalOpen, setModalOpen] = useState(false);
  const [editCase, setEditCase] = useState<CyberCase | null>(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [search, setSearch] = useState("");
  const [crimeType, setCrimeType] = useState("todos");
  const [dateFrom, setDateFrom] = useState("");
  const [dateTo, setDateTo] = useState("");

  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Debounce search to avoid too many API calls
  const debouncedSearch = useDebounce(search, 300);

  const limit = 20; // Items per page

  // Build query key for caching
  const queryKey = useMemo(() => [
    "/api/cyber-cases", 
    currentPage, 
    limit, 
    debouncedSearch, 
    crimeType, 
    dateFrom, 
    dateTo
  ], [currentPage, limit, debouncedSearch, crimeType, dateFrom, dateTo]);

  // Fetch cases with optimized query
  const { data, isLoading, error } = useQuery<CasesResponse>({
    queryKey,
    queryFn: async () => {
      const params = new URLSearchParams({
        page: currentPage.toString(),
        limit: limit.toString(),
        ...(debouncedSearch && { search: debouncedSearch }),
        ...(crimeType !== "todos" && { crimeType }),
        ...(dateFrom && { dateFrom }),
        ...(dateTo && { dateTo })
      });

      const response = await fetch(`/api/cyber-cases?${params}`);
      if (!response.ok) throw new Error('Failed to fetch cases');
      return response.json();
    },
    staleTime: 30000, // Cache for 30 seconds
    retry: 2,
  });

  // Optimized mutations
  const createMutation = useMutation({
    mutationFn: (newCase: any) =>
      apiRequest("/api/cyber-cases", "POST", newCase),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/cyber-cases"] });
      toast({ title: "Caso creado exitosamente" });
    },
    onError: () => {
      toast({ title: "Error al crear caso", variant: "destructive" });
    }
  });

  const updateMutation = useMutation({
    mutationFn: ({ id, data }: { id: string; data: any }) =>
      apiRequest(`/api/cyber-cases/${id}`, "PUT", data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/cyber-cases"] });
      toast({ title: "Caso actualizado exitosamente" });
    },
    onError: () => {
      toast({ title: "Error al actualizar caso", variant: "destructive" });
    }
  });

  const deleteMutation = useMutation({
    mutationFn: (id: string) =>
      apiRequest(`/api/cyber-cases/${id}`, "DELETE"),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/cyber-cases"] });
      toast({ title: "Caso eliminado exitosamente" });
    },
    onError: () => {
      toast({ title: "Error al eliminar caso", variant: "destructive" });
    }
  });

  // Optimized handlers
  const handleCreateCase = useCallback((caseData: any) => {
    createMutation.mutate(caseData);
    setModalOpen(false);
    setEditCase(null);
  }, [createMutation]);

  const handleEditCase = useCallback((caseData: any) => {
    if (!editCase?.id) return;
    updateMutation.mutate({ id: editCase.id, data: caseData });
    setModalOpen(false);
    setEditCase(null);
  }, [editCase?.id, updateMutation]);

  const handleDeleteCase = useCallback((id: string) => {
    if (confirm("¿Está seguro de que desea eliminar este caso?")) {
      deleteMutation.mutate(id);
    }
  }, [deleteMutation]);

  const resetFilters = useCallback(() => {
    setSearch("");
    setCrimeType("todos");
    setDateFrom("");
    setDateTo("");
    setCurrentPage(1);
  }, []);

  // Memoized components for performance
  const CaseRow = useMemo(() => ({ cyberCase }: { cyberCase: CyberCase }) => (
    <tr key={cyberCase.id} className="border-b border-border/20 hover:bg-primary/5 transition-colors">
      <td className="p-3 font-mono text-sm">{cyberCase.expedientNumber}</td>
      <td className="p-3">
        <Badge className={`${BADGE_COLORS.crimeType[cyberCase.crimeType as keyof typeof BADGE_COLORS.crimeType] || 'bg-gray-500/20 text-gray-400 border-gray-500/30'} font-mono text-xs`}>
          {cyberCase.crimeType}
        </Badge>
      </td>
      <td className="p-3 font-mono text-sm max-w-xs truncate">{cyberCase.victim}</td>
      <td className="p-3 font-mono text-sm">${cyberCase.stolenAmount?.toLocaleString() || "0"}</td>
      <td className="p-3">
        <Badge className={`${BADGE_COLORS.status[cyberCase.investigationStatus as keyof typeof BADGE_COLORS.status] || 'bg-gray-500/20 text-gray-400 border-gray-500/30'} font-mono text-xs`}>
          {cyberCase.investigationStatus}
        </Badge>
      </td>
      <td className="p-3 font-mono text-sm">{cyberCase.caseDate}</td>
      <td className="p-3">
        <div className="flex space-x-1">
          <Button
            variant="outline"
            size="sm"
            onClick={() => {
              setEditCase(cyberCase);
              setModalOpen(true);
            }}
            className="h-8 w-8 p-0 neon-border"
          >
            <Edit className="h-3 w-3" />
          </Button>
          <Button
            variant="outline"
            size="sm"
            className="h-8 w-8 p-0 neon-border"
          >
            <Eye className="h-3 w-3" />
          </Button>
          <Button
            variant="destructive"
            size="sm"
            onClick={() => handleDeleteCase(cyberCase.id)}
            className="h-8 w-8 p-0"
          >
            <Trash2 className="h-3 w-3" />
          </Button>
        </div>
      </td>
    </tr>
  ), [handleDeleteCase]);

  if (error) {
    return (
      <div className="p-6 text-center">
        <p className="text-destructive font-mono">Error al cargar casos</p>
        <Button onClick={() => window.location.reload()} className="mt-4">
          Recargar
        </Button>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-3xl font-bold matrix-text font-mono">[ GESTIÓN DE CASOS ]</h2>
          <p className="text-muted-foreground font-mono">Sistema de casos de delitos informáticos</p>
        </div>
        <Button
          onClick={() => {
            setEditCase(null);
            setModalOpen(true);
          }}
          className="neon-border font-mono"
        >
          <Plus className="w-4 h-4 mr-2" />
          NUEVO CASO
        </Button>
      </div>

      {/* Filters */}
      <Card className="neon-border theme-card">
        <CardContent className="p-4">
          <div className="grid grid-cols-1 md:grid-cols-5 gap-4">
            <div className="relative">
              <Search className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
              <Input
                placeholder="Buscar casos..."
                value={search}
                onChange={(e) => setSearch(e.target.value)}
                className="pl-10"
              />
            </div>
            <Select value={crimeType} onValueChange={setCrimeType}>
              <SelectTrigger>
                <SelectValue placeholder="Tipo de delito" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="todos">Todos los tipos</SelectItem>
                {CRIME_TYPES.map((type) => (
                  <SelectItem key={type} value={type}>{type}</SelectItem>
                ))}
              </SelectContent>
            </Select>
            <Input
              type="date"
              value={dateFrom}
              onChange={(e) => setDateFrom(e.target.value)}
              placeholder="Desde"
            />
            <Input
              type="date"
              value={dateTo}
              onChange={(e) => setDateTo(e.target.value)}
              placeholder="Hasta"
            />
            <Button onClick={resetFilters} variant="outline" className="font-mono">
              LIMPIAR
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Cases Table */}
      <Card className="neon-border theme-card">
        <CardContent className="p-0">
          {isLoading ? (
            <div className="p-6 space-y-4">
              {Array.from({ length: 5 }).map((_, i) => (
                <Skeleton key={i} className="h-16 w-full" />
              ))}
            </div>
          ) : (
            <>
              <div className="overflow-x-auto">
                <table className="w-full">
                  <thead className="bg-muted/20">
                    <tr>
                      <th className="p-3 text-left font-mono text-sm">EXPEDIENTE</th>
                      <th className="p-3 text-left font-mono text-sm">DELITO</th>
                      <th className="p-3 text-left font-mono text-sm">DESCRIPCIÓN</th>
                      <th className="p-3 text-left font-mono text-sm">MONTO</th>
                      <th className="p-3 text-left font-mono text-sm">ESTADO</th>
                      <th className="p-3 text-left font-mono text-sm">FECHA</th>
                      <th className="p-3 text-left font-mono text-sm">ACCIONES</th>
                    </tr>
                  </thead>
                  <tbody>
                    {data?.cases.map((cyberCase) => (
                      <CaseRow key={cyberCase.id} cyberCase={cyberCase} />
                    ))}
                  </tbody>
                </table>
              </div>

              {/* Pagination */}
              {data && data.totalPages > 1 && (
                <div className="flex items-center justify-between p-4 border-t border-border/20">
                  <div className="text-sm text-muted-foreground font-mono">
                    {data.cases.length} de {data.total} casos
                  </div>
                  <div className="flex items-center space-x-2">
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => setCurrentPage(p => Math.max(1, p - 1))}
                      disabled={currentPage === 1}
                      className="font-mono"
                    >
                      <ChevronLeft className="w-4 h-4" />
                    </Button>
                    <span className="text-sm font-mono">
                      {currentPage} / {data.totalPages}
                    </span>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => setCurrentPage(p => Math.min(data.totalPages, p + 1))}
                      disabled={currentPage === data.totalPages}
                      className="font-mono"
                    >
                      <ChevronRight className="w-4 h-4" />
                    </Button>
                  </div>
                </div>
              )}
            </>
          )}
        </CardContent>
      </Card>

      {/* Modal */}
      <CaseModal
        isOpen={modalOpen}
        onClose={() => {
          setModalOpen(false);
          setEditCase(null);
        }}
        onSubmit={editCase ? handleEditCase : handleCreateCase}
        initialData={editCase}
        isLoading={createMutation.isPending || updateMutation.isPending}
      />
    </div>
  );
}

client/src/components/DataManagementOpti.content:
import { useState, useMemo } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Skeleton } from "@/components/ui/skeleton";
import { Badge } from "@/components/ui/badge";
import { 
  Plus, 
  Search, 
  X, 
  Edit, 
  Trash2,
  ChevronLeft,
  ChevronRight,
  Eye
} from "lucide-react";
import { CaseModal } from "./CaseModal";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import type { CyberCase } from "@shared/schema";

interface CasesResponse {
  cases: CyberCase[];
  total: number;
}

interface FilterState {
  search: string;
  crimeType: string;
  dateFrom: string;
  dateTo: string;
  page: number;
  limit: number;
}

const CRIME_TYPES = [
  "Hacking",
  "Phishing", 
  "Malware",
  "Ransomware",
  "Fraude cibernético",
  "Robo de identidad",
  "Ciberacoso",
  "Suplantación de identidad",
];

const BADGE_COLORS = {
  crimeType: {
    "Hacking": "badge-danger",
    "Phishing": "badge-warning", 
    "Malware": "badge-danger",
    "Ransomware": "badge-danger",
    "Fraude cibernético": "badge-warning",
    "Robo de identidad": "badge-primary",
    "Ciberacoso": "badge-warning",
    "Suplantación de identidad": "badge-primary",
  },
  status: {
    "Pendiente": "badge-warning",
    "En proceso": "badge-primary",
    "Completado": "badge-success",
    "Sin respuesta": "badge-warning",
    "Rechazado": "badge-danger",
  }
} as const;

export function DataManagementOptimized() {
  const [modalOpen, setModalOpen] = useState(false);
  const [editCase, setEditCase] = useState<CyberCase | null>(null);
  const [filters, setFilters] = useState<FilterState>({
    search: "",
    crimeType: "",
    dateFrom: "",
    dateTo: "",
    page: 1,
    limit: 10,
  });

  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: casesData, isLoading, error } = useQuery<CasesResponse>({
    queryKey: ["/api/cyber-cases", filters],
    retry: false,
  });

  const deleteMutation = useMutation({
    mutationFn: (id: string) => apiRequest("DELETE", `/api/cyber-cases/${id}`),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/cyber-cases"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard/stats"] });
      toast({ title: "Éxito", description: "Caso eliminado correctamente" });
    },
    onError: () => {
      toast({ title: "Error", description: "No se pudo eliminar el caso", variant: "destructive" });
    },
  });

  const updateFilter = (key: keyof FilterState, value: string | number) => {
    setFilters(prev => ({ ...prev, [key]: value, ...(key !== 'page' && { page: 1 }) }));
  };

  const clearFilters = () => {
    setFilters({ search: "", crimeType: "", dateFrom: "", dateTo: "", page: 1, limit: 10 });
  };

  const openEditModal = (caseData: CyberCase) => {
    setModalOpen(false); // Asegurar que esté cerrado primero
    setTimeout(() => {
      setEditCase(caseData);
      setModalOpen(true);
    }, 50);
  };

  const viewCase = (caseData: CyberCase) => {
    // Para visualizar el caso, abrimos el modal en modo lectura
    setModalOpen(false); // Asegurar que esté cerrado primero
    setTimeout(() => {
      setEditCase({ ...caseData, readOnly: true } as any);
      setModalOpen(true);
    }, 50);
  };

  const closeModal = () => {
    setModalOpen(false);
    setEditCase(null);
  };

  const confirmDelete = (id: string) => {
    if (window.confirm("¿Está seguro de que desea eliminar este caso?")) {
      deleteMutation.mutate(id);
    }
  };

  const formatCurrency = (amount: string) => {
    return new Intl.NumberFormat('es-ES', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
    }).format(parseFloat(amount));
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('es-ES');
  };

  const getBadgeColor = (type: 'crimeType' | 'status', value: string) => {
    return BADGE_COLORS[type][value as keyof typeof BADGE_COLORS[typeof type]] || "badge-rejected";
  };

  const totalPages = Math.ceil((casesData?.total || 0) / filters.limit);
  const hasResults = casesData && casesData.cases.length > 0;

  if (isLoading) {
    return (
      <div className="p-6 space-y-6 bg-background min-h-screen">
        <div className="flex justify-between items-center">
          <div>
            <Skeleton className="h-8 w-64 mb-2" />
            <Skeleton className="h-4 w-96" />
          </div>
          <Skeleton className="h-10 w-32" />
        </div>
        <Card>
          <CardContent className="p-6">
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
              {Array.from({ length: 4 }).map((_, i) => (
                <Skeleton key={i} className="h-10" />
              ))}
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-6 space-y-4">
            {Array.from({ length: 5 }).map((_, i) => (
              <Skeleton key={i} className="h-16 w-full" />
            ))}
          </CardContent>
        </Card>
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-6">
        <div className="text-center py-12">
          <p className="text-red-600">Error al cargar los datos</p>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6 space-y-6 bg-background min-h-screen">
      {/* Header */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between">
        <div>
          <h2 className="text-2xl font-bold text-primary-custom font-mono">[ GESTIÓN DE CASOS ]</h2>
          <p className="text-primary-custom/70 font-mono">Sistema de casos de delitos informáticos</p>
        </div>
        <Button data-testid="button-new-case" onClick={() => setModalOpen(true)} className="mt-4 sm:mt-0 btn-primary">
          <Plus className="w-4 h-4 mr-2" />
          NUEVO CASO
        </Button>
      </div>

      {/* Filters */}
      <Card className="card-primary">
        <CardContent className="p-6">
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            <div className="relative">
              <Search className="w-4 h-4 absolute left-3 top-3 text-primary-custom" />
              <Input
                data-testid="input-search"
                placeholder="Buscar por expediente..."
                value={filters.search}
                onChange={(e) => updateFilter("search", e.target.value)}
                className="pl-10 input-primary"
              />
            </div>

            <Select value={filters.crimeType || "todos"} onValueChange={(value) => updateFilter("crimeType", value === "todos" ? "" : value)}>
              <SelectTrigger data-testid="select-crime-type" className="input-primary">
                <SelectValue placeholder="Tipo de delito" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="todos">Todos los delitos</SelectItem>
                {CRIME_TYPES.map((type) => (
                  <SelectItem key={type} value={type}>
                    {type}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>

            <Input
              data-testid="input-date-from"
              type="date"
              value={filters.dateFrom}
              onChange={(e) => updateFilter("dateFrom", e.target.value)}
              placeholder="Fecha desde"
              className="input-primary"
            />

            <Input
              data-testid="input-date-to"
              type="date"
              value={filters.dateTo}
              onChange={(e) => updateFilter("dateTo", e.target.value)}
              placeholder="Fecha hasta"
              className="input-primary"
            />
          </div>

          <div className="mt-4">
            <Button data-testid="button-clear" variant="outline" onClick={clearFilters} className="border-primary/30 text-primary-custom hover:bg-primary/10">
              <X className="w-4 h-4 mr-2" />
              LIMPIAR FILTROS
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Data Table */}
      <Card className="card-primary">
        <CardContent className="p-0">
          <div className="overflow-x-auto">
            <table className="w-full card-primary">
              <thead className="border-b">
                <tr>
                  <th className="px-6 py-4 text-left text-xs font-semibold uppercase text-primary-custom bg-muted border-b border-primary/30">FECHA</th>
                  <th className="px-6 py-4 text-left text-xs font-semibold uppercase text-primary-custom bg-muted border-b border-primary/30">N° EXPEDIENTE</th>
                  <th className="px-6 py-4 text-left text-xs font-semibold uppercase text-primary-custom bg-muted border-b border-primary/30">DELITO</th>
                  <th className="px-6 py-4 text-left text-xs font-semibold uppercase text-primary-custom bg-muted border-b border-primary/30">VÍCTIMA</th>
                  <th className="px-6 py-4 text-left text-xs font-semibold uppercase text-primary-custom bg-muted border-b border-primary/30">ESTADO</th>
                  <th className="px-6 py-4 text-left text-xs font-semibold uppercase text-primary-custom bg-muted border-b border-primary/30">MONTO</th>
                  <th className="px-6 py-4 text-left text-xs font-semibold uppercase text-primary-custom bg-muted border-b border-primary/30">ACCIONES</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-primary/20">
                {!hasResults ? (
                  <tr>
                    <td colSpan={7} className="px-6 py-12 text-center text-primary-custom/70">
                      &gt;&gt; NO SE ENCONTRARON CASOS EN EL SISTEMA &lt;&lt;
                    </td>
                  </tr>
                ) : (
                  casesData.cases.map((caseItem) => (
                    <tr key={caseItem.id} className="hover:bg-primary/10 transition-all duration-200 border-b border-primary/20">
                      <td className="px-6 py-4 text-sm text-foreground font-mono">
                        {formatDate(caseItem.caseDate)}
                      </td>
                      <td className="px-6 py-4">
                        <span className="text-sm font-medium text-primary-custom font-mono">
                          #{caseItem.expedientNumber}
                        </span>
                      </td>
                      <td className="px-6 py-4">
                        <Badge className={getBadgeColor('crimeType', caseItem.crimeType)}>
                          {caseItem.crimeType}
                        </Badge>
                      </td>
                      <td className="px-6 py-4 text-sm text-foreground max-w-xs truncate font-mono">
                        {caseItem.victim}
                      </td>
                      <td className="px-6 py-4">
                        <Badge className={getBadgeColor('status', caseItem.investigationStatus)}>
                          {caseItem.investigationStatus}
                        </Badge>
                      </td>
                      <td className="px-6 py-4 text-sm font-medium text-accent-custom font-mono">
                        {formatCurrency(caseItem.stolenAmount)}
                      </td>
                      <td className="px-6 py-4">
                        <Button
                          data-testid={`button-delete-${caseItem.id}`}
                          variant="ghost"
                          size="sm"
                          onClick={() => confirmDelete(caseItem.id)}
                          disabled={deleteMutation.isPending}
                          className="text-destructive hover:text-destructive/80 hover:bg-destructive/10"
                        >
                          <Trash2 className="w-4 h-4" />
                        </Button>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>

          {/* Pagination */}
          {hasResults && totalPages > 1 && (
            <div className="bg-muted px-6 py-4 border-t border-primary/30 flex items-center justify-between">
              <div className="text-sm text-primary-custom/70 font-mono">
                &gt;&gt; MOSTRANDO {((filters.page - 1) * filters.limit) + 1}-{Math.min(filters.page * filters.limit, casesData.total)} DE {casesData.total} REGISTROS
              </div>
              <div className="flex items-center space-x-2">
                <Button
                  data-testid="button-prev-page"
                  variant="outline"
                  size="sm"
                  onClick={() => updateFilter("page", filters.page - 1)}
                  disabled={filters.page <= 1}
                  className="border-primary/30 text-primary-custom hover:bg-primary/10"
                >
                  <ChevronLeft className="w-4 h-4" />
                  ANTERIOR
                </Button>
                <span className="text-sm text-primary-custom font-mono">
                  [ {filters.page} / {totalPages} ]
                </span>
                <Button
                  data-testid="button-next-page"
                  variant="outline"
                  size="sm"
                  onClick={() => updateFilter("page", filters.page + 1)}
                  disabled={filters.page >= totalPages}
                  className="border-primary/30 text-primary-custom hover:bg-primary/10"
                >
                  SIGUIENTE
                  <ChevronRight className="w-4 h-4" />
                </Button>
              </div>
            </div>
          )}
        </CardContent>
      </Card>

      <CaseModal
        isOpen={modalOpen}
        onClose={closeModal}
        onSubmit={() => {}}
        initialData={editCase}
      />
    </div>
  );
}

